INSERT INTO `tbl_event` (`id`, `typeevent_id`, `name`, `content`, `place`, `thumbnail`, `start_time`, `end_time`, `number_guest`, `dimention`, `create_time`, `update_time`, `create_user_id`, `view`, `censor`, `slide_small`, `slide_lage`, `e_like`) VALUES
(1, 8, 'Khóa huấn luyện đặc biệt “ KHAI SÁNG NGUỒN NĂNG LƯỢNG VÔ HẠN TRONG BẠN” ', '<div class="event-desc">\r\n						<div class="ctitle">Thông tin sự kiện<b> Khóa huấn luyện đặc biệt “ KHAI SÁNG NGUỒN NĂNG LƯỢNG VÔ HẠN TRONG BẠN” </b></div>\r\n						<p>\r\n	Khóa huấn luyện đặc biệt<span style="color: #ff6600;"><strong> “ KHAI SÁNG NGUỒN NĂNG LƯỢNG VÔ HẠN TRONG BẠN”</strong> </span>sẽ\r\n giúp bạn trải nghiệm một cuộc tìm kiếm không ngừng để phục hồi ước mơ \r\nvà biến nó thành hiện thực, nhắc nhở chúng ta nhớ lại và sử dụng nguồn \r\nnăng lượng vô biên còn đang ngủ yên trong mỗi người chúng ta.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<img alt="Khai sáng nguồn năng lượng trong bạn - nguyễn mạnh hùng thái hà books" src="http://datve.sukienhay.com/images/su-kien/dao-tao/2012/t4/nguyen-manh-hung/khaisang_nguonnangluong2.jpg" style="display: block; margin-left: auto; margin-right: auto;" title="Khai sáng nguồn năng lượng trong bạn - nguyễn mạnh hùng thái hà books" border="0" height="378" width="600"><br>\r\n	<br>\r\n	Chúng ta ai ai cũng có những ước mơ...Ai ai cũng muốn tin vững tận đáy \r\nlòng rằng mình có một năng khiếu riêng. Có khả năng thay đổi, có thể gây\r\n ảnh hưởng đặc biệt trên người khác và có thể biến đổi thế giới này trở \r\nnên tốt hơn. Có những lúc trong cuộc đời, chúng ta ai ai cũng có một \r\nviễn ảnh về cuộc sống tươi đẹp hơn mà chúng ta ao ước và đáng được \r\nhưởng. Thế nhưng, với nhiều người trong chúng ta, những ước mơ đó bị bao\r\n phủ dày đặc bởi những thất vọng và những những cái tầm thường của cuộc \r\nsống khiến chúng ta thậm chí không còn ý chí để cố gắng vươn lên. với \r\nquá nhiều người, những những ước mơ bị tan vỡ-và cùng với nó, ý muốn làm\r\n nên vận mệnh của mình cũng tan vỡ theo. Nhiều người đã mất niềm tin \r\nvững chắc, là yếu tố sắc bén của thành công. Chương trình huấn luyện đặc\r\n biệt “ KHAI SÁNG NGUỒN NĂNG LƯỢNG VÔ HẠN TRONG BẠN” sẽ giúp bạn trải \r\nnghiệm một cuộc tìm kiếm không ngừng để phục hồi ước mơ và biến nó thành\r\n hiện thực, nhắc nhở chúng ta nhớ lại và sử dụng nguồn năng lượng vô \r\nbiên còn đang ngủ yên trong mỗi người chúng ta.</p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong>PHÍ THAM DỰ: <span style="text-decoration: line-through;">2.100.000</span> VNĐ</strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong>Đăng ký tại Sự Kiện Hay chỉ&nbsp; 990.000 VNĐ</strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong><span style="font-size: small;">Mạng cộng đồng Sự Kiện Hay tặng 01 vé tham dự Offline Game Cashflow trị giá 100.000 VNĐ</span></strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium; color: #ff0000;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong>Hotline: <span style="font-size: medium;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong><span style="font-size: medium;"><span style="font-size: 16px;"><strong>0983 15 89 87</strong></span></span></strong></span></span></span></strong></span> / 0948 11 44 71</strong></span></span></span></strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong><a href="http://datve.sukienhay.com/c/khoa-hoc-khai-sang-nguon-nang-luong-vo-han-trong-ban-17.html" target="_blank"><span style="font-size: small;"><strong><img alt="dangky-sukienhay" src="http://sukienhay.com/images/stories/logo/dangky-sukienhay.png" style="border: 0px none currentcolor; display: block; margin-left: auto; margin-right: auto;" title="dangky-sukienhay" border="0" height="75" width="233"></strong></span></a></strong></span></span></span></strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="color: #ff6600; font-size: small;"><em><strong><img src="http://datve.sukienhay.com/images/icon/love-icon.gif" style="float: left;" border="0" width="40"></strong></em></span></p>\r\n<p style="text-align: left;">\r\n	<span style="color: #ff6600; font-size: small;"><em><strong>&nbsp;&nbsp; </strong></em></span></p>\r\n<p style="text-align: left;">\r\n	<span style="color: #ff6600; font-size: small;"><em><strong>Cuộc đời và tương lai của bạn sẽ hoàn toàn thay đổi nếu bạn tham gia khóa học 01 ngày duy nhất</strong></em></span></p>\r\n<p>\r\n	&nbsp;<br>\r\n	<strong>Thời gian:</strong> Từ 08h – 17h30 ngày 15/04/2012 (Chủ nhật)<br>\r\n	<strong>Địa điểm:</strong> Hội trường H3, trường Cao đẳng du lịch, 250 Hoàng Quốc Việt Hà Nội</p>\r\n<p>\r\n	<br>\r\n	Bạn sẽ nhận thấy được rất nhiều điều tốt đẹp trong cuộc sống đang chờ bạn<br>\r\n	&nbsp;<br>\r\n	Bạn sẽ đón chào ngày mới với nguồn năng lượng hứng khởi<br>\r\n	<br>\r\n	Bạn sẽ chuyển đổi từ những điều tẻ nhạt, đơn điệu sang những điều tràn đầy cảm xúc tích cực<br>\r\n	<br>\r\n	Bạn sẽ thành công và có tất cả những gì mình muốn</p>\r\n<p>\r\n	<br>\r\n	Có bao giờ bạn tự hỏi tại sao có những người trở nên giàu có rất dễ \r\ndàng trong khi những người khác lại sống cuộc sống nghèo đói, khó \r\nkhăn?</p>\r\n<p>\r\n	Có bao giờ bạn nghĩ, tại sao người ta hạnh phúc vậy? Bạn có quan sát và thấy những người đã thành công và đang sẽ thành công?</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Điều gì tạo nên sự khác biệt giữa người thành công và người thất \r\nbại? Giữa người hạnh phúc và bất hạnh?Có phải điều này là do sự khác \r\nbiệt về giáo dục hay tri thức? Hay là do kỹ năng hoặc thời thế?Hoặc là \r\ndo thói quen công việc, các mối quan hệ hay chỉ đơn giản là họ may mắn? \r\nLiệu đó có phải do sự lựa chọn lĩnh vực kinh doanh, công việc hay gia \r\nđình? Có phải do xuất phát điểm của mình thấp hơn họ?</p>\r\n<p>\r\n	<br>\r\n	Câu trả lời cho những câu hỏi này là KHÔNG PHẢI NHỮNG LÍ DO ĐÃ ĐƯỢC NÊU\r\n TRÊN. Chỉ có 1 lý do duy nhất là NHỮNG THÓI QUEN HÀNG NGÀY ĐÃ ĐƯỢC CÀI\r\n ĐẶT TRONG TIỀM THỨC CỦA BẠN, NẰM SÂU TRONG TÀNG THỨC CỦA BẠN. Những \r\nthói quen này đang giết chết bạn từng ngày, THẬM CHÍ TỪNG PHÚT, nếu \r\nnhư bạn không biết cách cài đặt lại và kiểm soát nó.</p>\r\n<p>\r\n	<br>\r\n	Nếu bạn đã sẵn sàng thay đổi cuộc sống của chính bản thân mình, \r\nthay đổi đời sống tài chính, sức khỏe, mối quan hệ, sự nghiệp, phát\r\n triển tiềm năng bản thân lên một tầm cao mới thì hãy đăng ký khóa \r\nhọc 01 ngày duy nhất.</p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<p>\r\n	<img alt="Thay đổi cuộc sống - làm chủ vận mệnh" src="http://datve.sukienhay.com/images/su-kien/hoi-thao/t3/quach-tuan-khanh-2204/changelife.jpg" style="display: block; margin-left: auto; margin-right: auto; text-align: center;" title="Thay đổi cuộc sống - làm chủ vận mệnh" border="0"></p>\r\n<p>\r\n	&nbsp;<br>\r\n	<strong><span style="color: #ff6600; font-size: small;"><em><strong><img src="http://datve.sukienhay.com/images/icon/love-icon.gif" style="float: left;" border="0" width="30"></strong></em></span></strong></p>\r\n<p>\r\n	<strong>&nbsp;<span style="font-size: medium;"> Bạn sẽ học được:</span></strong><br>\r\n	&nbsp;<br>\r\n	+&nbsp; Xậy dựng niềm tin<br>\r\n	+ Kích, khóa NEO năng lượng<br>\r\n	+ Tự ám thị để khẳng định mình là duy nhất<br>\r\n	+&nbsp; Cải thiện sức khỏe<br>\r\n	+&nbsp; Phát triển tình bạn, cải thiện những mối quan hệ hiện thời với đồng nghiệp, gia đình, bạn bè<br>\r\n	+&nbsp; Phương pháp quản lý tiền đơn giản, hiệu quả<br>\r\n	+&nbsp; Điều gì tạo nên con người bạn chế trong bạn (ý thức – tiềm thức)<br>\r\n	+&nbsp; Học cách tư duy tích cực trong mọi hoàn cảnh<br>\r\n	+&nbsp; Cách tạo lập mục tiêu cuộc đời<br>\r\n	+&nbsp; Phương pháp thiền tọa, thiền hành, thiền buông thư<br>\r\n	+&nbsp; Thực hành tăng sức khỏe cùng Dịch Cân Kinh do Đạt Ma Tổ Sư sáng lập<br>\r\n	+&nbsp; Thực hành Yoga</p>\r\n<p>\r\n	<br>\r\n	Tham gia sự kiện đặc biệt này bạn sẽ tự tin làm chủ cuộc sống của \r\nbạn. Nhất là bạn sẽ được trải nghiệm cùng một tập thể khát khao \r\nkhám phá chính bản thân mình và được cảm nhận chương trình giúp Thay \r\nđổi cuộc sống.<br>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium; color: #ff6600;"><strong>KHÓA HỌC&nbsp; KHAI SÁNG NGUỒN NĂNG LƯỢNG VÔ HẠN TRONG BẠN</strong> </span></p>\r\n<p style="text-align: center;">\r\n	<br>\r\n	<span style="font-size: medium;"><strong>PHÍ THAM DỰ: <span style="text-decoration: line-through;">2.100.000</span> VNĐ</strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong>Đăng ký tại Sự Kiện Hay chỉ&nbsp; 990.000 VNĐ</strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong><span style="font-size: small;">Mạng cộng đồng Sự Kiện Hay tặng 01 vé tham dự Offline Game Cashflow trị giá 100.000 VNĐ</span></strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium; color: #ff0000;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong>Hotline: <span style="font-size: medium;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong><span style="font-size: medium;"><span style="font-size: 16px;"><strong>0983 15 89 87</strong></span></span></strong></span></span></span></strong></span> / 0948 11 44 71</strong></span></span></span></strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong><a href="http://datve.sukienhay.com/c/khoa-hoc-khai-sang-nguon-nang-luong-vo-han-trong-ban-17.html" target="_blank"><span style="font-size: small;"><strong><img alt="dangky-sukienhay" src="http://sukienhay.com/images/stories/logo/dangky-sukienhay.png" style="border: 0px none currentcolor; display: block; margin-left: auto; margin-right: auto;" title="dangky-sukienhay" border="0" height="75" width="233"></strong></span></a></strong></span></span></span></strong></span></p>\r\n<p>\r\n	<strong>Qùa tặng đặc biệt khác cho bạn khi đăng ký tham gia khóa học này:</strong></p>\r\n<p>\r\n	+&nbsp; 01 tranh Phật<br>\r\n	+&nbsp; 01 Kinh nhật tụng<br>\r\n	+&nbsp; 01 Cuốn sách có chữ ký của Diễn giả Nguyễn Mạnh Hùng<br>\r\n	+&nbsp; 01 suất ăn chay buổi trưa trong chương trình<br>\r\n	&nbsp;<br>\r\n	<strong><strong><span style="color: #ff6600; font-size: small;"><em><strong><img src="http://datve.sukienhay.com/images/icon/love-icon.gif" border="0" width="30"></strong></em></span></strong> Thông tin Diễn Giả:&nbsp;<span style="font-size: small;"><strong>&nbsp;Nguyễn Mạnh Hùng</strong></span></strong></p>\r\n<table style="width: 518px; height: 745px;" border="1" cellpadding="10" cellspacing="10">\r\n	<tbody>\r\n		<tr>\r\n			<td style="width: 227px;">\r\n				<p>\r\n					<strong><img alt="Tiến sĩ diễn giả nguyễn mạnh hùng" src="http://datve.sukienhay.com/images/su-kien/dao-tao/2012/t4/nguyen-manh-hung/ts-nguyen-manh-hung-thb.jpg" style="margin: 5px; width: 207px; height: 372px;" title="Tiến sĩ diễn giả nguyễn mạnh hùng" border="0"></strong></p>\r\n			</td>\r\n			<td style="width: 230px;">\r\n				<span style="font-size: small;">- Kiếm được 1 triệu USD đầu tiên trước 30 tuổi.</span><br>\r\n				<span style="font-size: small;">- Đã học tập, công tác, làm việc và có mặt tại 41 quốc gia trên thế giới.</span><br>\r\n				<span style="font-size: small;">- Biết 4 ngoại ngữ: Anh, Pháp, Nga, Trung.</span><br>\r\n				<span style="font-size: small;">- Tổng số thời gian sống ở nước ngoài là gần 16 năm.</span><br>\r\n				<span style="font-size: small;">- Có 12 năm làm việc tại FPT (từ 1995 đến 2008).</span><br>\r\n				<span style="font-size: small;">- Sáng lập và lãnh đạo nhiều doanh nghiệp, trong đó có Công ty CP Sách Thái&nbsp;&nbsp;&nbsp; Hà.</span><br>\r\n				<span style="font-size: small;">- Là Ủy viên BCH Trung ương hội Bảo vệ Quyền Trẻ em Việt Nam, UV BCH TW&nbsp;&nbsp; Hội Xuất bản Việt Nam.</span><br>\r\n				<span style="font-size: small;">- Chuyên gia tư vấn, huấn luyện, giảng dạy cho nhiều tập đoàn, doanh nghiệp, trung tâm đào tạo trong và ngoài nước.</span><br>\r\n				<span style="font-size: small;">- Đã xuất gia gieo duyên làm nhà sư\r\n trong vòng 7 ngày với&nbsp; thiền sư nổi tiếng Ashin Tejaniya - viện \r\ntrưởng thiền viện Shwe Oo Min Dhammasukha (Miến Điện). Đã tham gia \r\nkhóa thiền và raj yoga tại núi Abu Mount (Ấn Độ) với ngài Dadi Janky.\r\n Đã tham gia nhiều khóa thiền vipassana 10 ngày.</span><br>\r\n				<span style="font-size: small;">- Các tổ chức đã giảng dạy Tổ chức \r\nInWent (Đức), Hội Doanh nhân trẻ Hà Nội, Hiệp hội Nữ Doanh nhân Hà Nội; \r\nCác công ty và Trung tâm đào tạo: Đào tạo&nbsp; và Tư vấn ngân hàng BTC, PTI,\r\n Vitoria, VCCI, Viện quản lý Châu Á, Công ty chứng khoán Thăng Long, \r\nChứng khoán Đại Dương, Công ty Thành Nam, Công ty Hà An, Ngân hàng Quân \r\nđội MB, Ngân hàng nhà Habubank, VIP Bank, ngân hàng An Bình, \r\nTechcombank, Tổng công ty Phát hành sách TP HCM Fahasa</span><br>\r\n				<span style="font-size: small;">- Sách đã xuất bản: Bài học từ \r\nngười quyét rác. Sắp xuất bản: Tâm trong tâm, Bí quyết khởi nghiệp, \r\nChữa bệnh không cần thuốc, Đọc sách siêu tốc.</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p align="center">\r\n	&nbsp;</p>\r\n<p>\r\n	Khóa huấn luyện đặc biệt<span style="color: #ff6600;"><strong> “ KHAI SÁNG NGUỒN NĂNG LƯỢNG VÔ HẠN TRONG BẠN”</strong> </span>sẽ\r\n giúp bạn trải nghiệm một cuộc tìm kiếm không ngừng để phục hồi ước mơ \r\nvà biến nó thành hiện thực, nhắc nhở chúng ta nhớ lại và sử dụng nguồn \r\nnăng lượng vô biên còn đang ngủ yên trong mỗi người chúng ta.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<img alt="Khai sáng nguồn năng lượng trong bạn - nguyễn mạnh hùng thái hà books" src="http://datve.sukienhay.com/images/su-kien/dao-tao/2012/t4/nguyen-manh-hung/khaisang_nguonnangluong2.jpg" style="display: block; margin-left: auto; margin-right: auto;" title="Khai sáng nguồn năng lượng trong bạn - nguyễn mạnh hùng thái hà books" border="0" height="378" width="600"><br>\r\n	<br>\r\n	Chúng ta ai ai cũng có những ước mơ...Ai ai cũng muốn tin vững tận đáy \r\nlòng rằng mình có một năng khiếu riêng. Có khả năng thay đổi, có thể gây\r\n ảnh hưởng đặc biệt trên người khác và có thể biến đổi thế giới này trở \r\nnên tốt hơn. Có những lúc trong cuộc đời, chúng ta ai ai cũng có một \r\nviễn ảnh về cuộc sống tươi đẹp hơn mà chúng ta ao ước và đáng được \r\nhưởng. Thế nhưng, với nhiều người trong chúng ta, những ước mơ đó bị bao\r\n phủ dày đặc bởi những thất vọng và những những cái tầm thường của cuộc \r\nsống khiến chúng ta thậm chí không còn ý chí để cố gắng vươn lên. với \r\nquá nhiều người, những những ước mơ bị tan vỡ-và cùng với nó, ý muốn làm\r\n nên vận mệnh của mình cũng tan vỡ theo. Nhiều người đã mất niềm tin \r\nvững chắc, là yếu tố sắc bén của thành công. Chương trình huấn luyện đặc\r\n biệt “ KHAI SÁNG NGUỒN NĂNG LƯỢNG VÔ HẠN TRONG BẠN” sẽ giúp bạn trải \r\nnghiệm một cuộc tìm kiếm không ngừng để phục hồi ước mơ và biến nó thành\r\n hiện thực, nhắc nhở chúng ta nhớ lại và sử dụng nguồn năng lượng vô \r\nbiên còn đang ngủ yên trong mỗi người chúng ta.&nbsp;</p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong>PHÍ THAM DỰ: <span style="text-decoration: line-through;">2.100.000</span> VNĐ</strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong>Đăng ký tại Sự Kiện Hay chỉ&nbsp; 990.000 VNĐ</strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong><span style="font-size: small;">Mạng cộng đồng Sự Kiện Hay tặng 01 vé tham dự Offline Game Cashflow trị giá 100.000 VNĐ</span></strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium; color: #ff0000;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong>Hotline: <span style="font-size: medium;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong><span style="font-size: medium;"><span style="font-size: 16px;"><strong>0983 15 89 87</strong></span></span></strong></span></span></span></strong></span> / 0948 11 44 71</strong></span></span></span></strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong><a href="http://datve.sukienhay.com/c/khoa-hoc-khai-sang-nguon-nang-luong-vo-han-trong-ban-17.html"><span style="font-size: small;"><strong><img alt="dangky-sukienhay" src="http://sukienhay.com/images/stories/logo/dangky-sukienhay.png" style="border: 0px none currentcolor; display: block; margin-left: auto; margin-right: auto;" title="dangky-sukienhay" border="0" height="75" width="233"></strong></span></a></strong></span></span></span></strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="color: #ff6600; font-size: small;"><em><strong><img src="http://datve.sukienhay.com/images/icon/love-icon.gif" style="float: left;" border="0" width="40"></strong></em></span></p>\r\n<p style="text-align: left;">\r\n	<span style="color: #ff6600; font-size: small;"><em><strong>&nbsp;&nbsp; </strong></em></span></p>\r\n<p style="text-align: left;">\r\n	<span style="color: #ff6600; font-size: small;"><em><strong>Cuộc đời và tương lai của bạn sẽ hoàn toàn thay đổi nếu bạn tham gia khóa học 01 ngày duy nhất</strong></em></span></p>\r\n<p>\r\n	&nbsp;<br>\r\n	<strong>Thời gian:</strong> Từ 08h – 17h30 ngày 15/04/2012 (Chủ nhật)<br>\r\n	<strong>Địa điểm:</strong> Hội trường H3, trường Cao đẳng du lịch, 250 Hoàng Quốc Việt Hà Nội</p>\r\n<p>\r\n	<br>\r\n	Bạn sẽ nhận thấy được rất nhiều điều tốt đẹp trong cuộc sống đang chờ bạn<br>\r\n	&nbsp;<br>\r\n	Bạn sẽ đón chào ngày mới với nguồn năng lượng hứng khởi<br>\r\n	<br>\r\n	Bạn sẽ chuyển đổi từ những điều tẻ nhạt, đơn điệu sang những điều tràn đầy cảm xúc tích cực<br>\r\n	<br>\r\n	Bạn sẽ thành công và có tất cả những gì mình muốn</p>\r\n<p>\r\n	<br>\r\n	Có bao giờ bạn tự hỏi tại sao có những người trở nên giàu có rất dễ \r\ndàng trong khi những người khác lại sống cuộc sống nghèo đói, khó \r\nkhăn?</p>\r\n<p>\r\n	Có bao giờ bạn nghĩ, tại sao người ta hạnh phúc vậy? Bạn có quan sát và thấy những người đã thành công và đang sẽ thành công?</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Điều gì tạo nên sự khác biệt giữa người thành công và người thất \r\nbại? Giữa người hạnh phúc và bất hạnh?Có phải điều này là do sự khác \r\nbiệt về giáo dục hay tri thức? Hay là do kỹ năng hoặc thời thế?Hoặc là \r\ndo thói quen công việc, các mối quan hệ hay chỉ đơn giản là họ may mắn? \r\nLiệu đó có phải do sự lựa chọn lĩnh vực kinh doanh, công việc hay gia \r\nđình? Có phải do xuất phát điểm của mình thấp hơn họ?</p>\r\n<p>\r\n	<br>\r\n	Câu trả lời cho những câu hỏi này là KHÔNG PHẢI NHỮNG LÍ DO ĐÃ ĐƯỢC NÊU\r\n TRÊN. Chỉ có 1 lý do duy nhất là NHỮNG THÓI QUEN HÀNG NGÀY ĐÃ ĐƯỢC CÀI\r\n ĐẶT TRONG TIỀM THỨC CỦA BẠN, NẰM SÂU TRONG TÀNG THỨC CỦA BẠN. Những \r\nthói quen này đang giết chết bạn từng ngày, THẬM CHÍ TỪNG PHÚT, nếu \r\nnhư bạn không biết cách cài đặt lại và kiểm soát nó.</p>\r\n<p>\r\n	<br>\r\n	Nếu bạn đã sẵn sàng thay đổi cuộc sống của chính bản thân mình, \r\nthay đổi đời sống tài chính, sức khỏe, mối quan hệ, sự nghiệp, phát\r\n triển tiềm năng bản thân lên một tầm cao mới thì hãy đăng ký khóa \r\nhọc 01 ngày duy nhất.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<img alt="Thay đổi cuộc sống - làm chủ vận mệnh" src="http://datve.sukienhay.com/images/su-kien/hoi-thao/t3/quach-tuan-khanh-2204/changelife.jpg" style="display: block; margin-left: auto; margin-right: auto;" title="Thay đổi cuộc sống - làm chủ vận mệnh" border="0"></p>\r\n<p>\r\n	&nbsp;<br>\r\n	<strong><span style="color: #ff6600; font-size: small;"><em><strong><img src="http://datve.sukienhay.com/images/icon/love-icon.gif" style="float: left;" border="0" width="30"></strong></em></span></strong></p>\r\n<p>\r\n	<strong>&nbsp;<span style="font-size: medium;"> Bạn sẽ học được:</span></strong><br>\r\n	&nbsp;<br>\r\n	+&nbsp; Xậy dựng niềm tin<br>\r\n	+ Kích, khóa NEO năng lượng<br>\r\n	+ Tự ám thị để khẳng định mình là duy nhất<br>\r\n	+&nbsp; Cải thiện sức khỏe<br>\r\n	+&nbsp; Phát triển tình bạn, cải thiện những mối quan hệ hiện thời với đồng nghiệp, gia đình, bạn bè<br>\r\n	+&nbsp; Phương pháp quản lý tiền đơn giản, hiệu quả<br>\r\n	+&nbsp; Điều gì tạo nên con người bạn chế trong bạn (ý thức – tiềm thức)<br>\r\n	+&nbsp; Học cách tư duy tích cực trong mọi hoàn cảnh<br>\r\n	+&nbsp; Cách tạo lập mục tiêu cuộc đời<br>\r\n	+&nbsp; Phương pháp thiền tọa, thiền hành, thiền buông thư<br>\r\n	+&nbsp; Thực hành tăng sức khỏe cùng Dịch Cân Kinh do Đạt Ma Tổ Sư sáng lập<br>\r\n	+&nbsp; Thực hành Yoga</p>\r\n<p>\r\n	<br>\r\n	Tham gia sự kiện đặc biệt này bạn sẽ tự tin làm chủ cuộc sống của \r\nbạn. Nhất là bạn sẽ được trải nghiệm cùng một tập thể khát khao \r\nkhám phá chính bản thân mình và được cảm nhận chương trình giúp Thay \r\nđổi cuộc sống.<br>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium; color: #ff6600;"><strong>KHÓA HỌC&nbsp; KHAI SÁNG NGUỒN NĂNG LƯỢNG VÔ HẠN TRONG BẠN</strong> </span></p>\r\n<p style="text-align: center;">\r\n	<br>\r\n	<span style="font-size: medium;"><strong>PHÍ THAM DỰ: <span style="text-decoration: line-through;">2.100.000</span> VNĐ</strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong>Đăng ký tại Sự Kiện Hay chỉ&nbsp; 990.000 VNĐ</strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong><span style="font-size: small;">Mạng cộng đồng Sự Kiện Hay tặng 01 vé tham dự Offline Game Cashflow trị giá 100.000 VNĐ</span></strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium; color: #ff0000;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong>Hotline: <span style="font-size: medium;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong><span style="font-size: medium;"><span style="font-size: 16px;"><strong>0983 15 89 87</strong></span></span></strong></span></span></span></strong></span> / 0948 11 44 71</strong></span></span></span></strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong><a href="http://datve.sukienhay.com/c/khoa-hoc-khai-sang-nguon-nang-luong-vo-han-trong-ban-17.html"><span style="font-size: small;"><strong><img alt="dangky-sukienhay" src="http://sukienhay.com/images/stories/logo/dangky-sukienhay.png" style="border: 0px none currentcolor; display: block; margin-left: auto; margin-right: auto;" title="dangky-sukienhay" border="0" height="75" width="233"></strong></span></a></strong></span></span></span></strong></span></p>\r\n<p>\r\n	<strong>Qùa tặng đặc biệt khác cho bạn khi đăng ký tham gia khóa học này:</strong></p>\r\n<p>\r\n	+&nbsp; 01 tranh Phật<br>\r\n	+&nbsp; 01 Kinh nhật tụng<br>\r\n	+&nbsp; 01 Cuốn sách có chữ ký của Diễn giả Nguyễn Mạnh Hùng<br>\r\n	+&nbsp; 01 suất ăn chay buổi trưa trong chương trình<br>\r\n	&nbsp;<br>\r\n	<strong><strong><span style="color: #ff6600; font-size: small;"><em><strong><img src="http://datve.sukienhay.com/images/icon/love-icon.gif" border="0" width="30"></strong></em></span></strong> Thông tin Diễn Giả:&nbsp;<span style="font-size: small;"><strong>&nbsp;Nguyễn Mạnh Hùng</strong></span></strong></p>\r\n<table border="1" cellpadding="10" cellspacing="10">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<p>\r\n					<strong><img alt="Tiến sĩ diễn giả nguyễn mạnh hùng" src="http://datve.sukienhay.com/images/su-kien/dao-tao/2012/t4/nguyen-manh-hung/ts-nguyen-manh-hung-thb.jpg" style="margin: 5px;" title="Tiến sĩ diễn giả nguyễn mạnh hùng" border="0" width="230"></strong></p>\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n			<td>\r\n				<span style="font-size: small;">- Kiếm được 1 triệu USD đầu tiên trước 30 tuổi.</span><br>\r\n				<span style="font-size: small;">- Đã học tập, công tác, làm việc và có mặt tại 41 quốc gia trên thế giới.</span><br>\r\n				<span style="font-size: small;">- Biết 4 ngoại ngữ: Anh, Pháp, Nga, Trung.</span><br>\r\n				<span style="font-size: small;">- Tổng số thời gian sống ở nước ngoài là gần 16 năm.</span><br>\r\n				<span style="font-size: small;">- Có 12 năm làm việc tại FPT (từ 1995 đến 2008).</span><br>\r\n				<span style="font-size: small;">- Sáng lập và lãnh đạo nhiều doanh nghiệp, trong đó có Công ty CP Sách Thái&nbsp;&nbsp;&nbsp; Hà.</span><br>\r\n				<span style="font-size: small;">- Là Ủy viên BCH Trung ương hội Bảo vệ Quyền Trẻ em Việt Nam, UV BCH TW&nbsp;&nbsp; Hội Xuất bản Việt Nam.</span><br>\r\n				<span style="font-size: small;">- Chuyên gia tư vấn, huấn luyện, giảng dạy cho nhiều tập đoàn, doanh nghiệp, trung tâm đào tạo trong và ngoài nước.</span><br>\r\n				<span style="font-size: small;">- Đã xuất gia gieo duyên làm nhà sư\r\n trong vòng 7 ngày với&nbsp; thiền sư nổi tiếng Ashin Tejaniya - viện \r\ntrưởng thiền viện Shwe Oo Min Dhammasukha (Miến Điện). Đã tham gia \r\nkhóa thiền và raj yoga tại núi Abu Mount (Ấn Độ) với ngài Dadi Janky.\r\n Đã tham gia nhiều khóa thiền vipassana 10 ngày.</span><br>\r\n				<span style="font-size: small;">- Các tổ chức đã giảng dạy Tổ chức \r\nInWent (Đức), Hội Doanh nhân trẻ Hà Nội, Hiệp hội Nữ Doanh nhân Hà Nội; \r\nCác công ty và Trung tâm đào tạo: Đào tạo&nbsp; và Tư vấn ngân hàng BTC, PTI,\r\n Vitoria, VCCI, Viện quản lý Châu Á, Công ty chứng khoán Thăng Long, \r\nChứng khoán Đại Dương, Công ty Thành Nam, Công ty Hà An, Ngân hàng Quân \r\nđội MB, Ngân hàng nhà Habubank, VIP Bank, ngân hàng An Bình, \r\nTechcombank, Tổng công ty Phát hành sách TP HCM Fahasa</span><br>\r\n				<span style="font-size: small;">- Sách đã xuất bản: Bài học từ \r\nngười quyét rác. Sắp xuất bản: Tâm trong tâm, Bí quyết khởi nghiệp, \r\nChữa bệnh không cần thuốc, Đọc sách siêu tốc.</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p align="center">\r\n	&nbsp;</p>\r\n<p style="text-align: left;" align="center">\r\n	<strong><span style="font-size: medium;"><strong><span style="line-height: 150%; font-family: ''Times New Roman'',''serif''; color: #0070c0;">HÃY LẮNG NGHE CẢM NHẬN CỦA HỌC VIÊN TRẢI NGHIỆM&nbsp; SAU KHÓA HỌC. HÃY ĐẾN ĐỂ THẤY!</span></strong></span></strong></p>\r\n<p style="text-align: left;" align="center">\r\n	“ Sau khóa học tôi đã trở thành con người khác. Con người mới được tái \r\nsinh. Tôi cảm ơn cha mẹ tôi đã sinh ra tôi. Tôi cảm ơn vũ trụ đã cho tôi\r\n cơ hội có mặt trên đời này. Tôi là con người tuyệt vời, thông mình, \r\nthành công, là số 1, là duy nhất và không có lí gì tôi không trở thành \r\ncon người thành đạt, giàu có và hạnh phúc của cuộc đời này.”</p>\r\n<p align="right">\r\n	<strong>Tạ Thị Lan – VP Đại diện công ty Ever Neuro Pharma – Austria</strong></p>\r\n<p align="right">\r\n	&nbsp;</p>\r\n<p style="text-align: left;" align="center">\r\n	“Tôi đã biết mình là ai, mình từ đâu đến, tôi biết rằng bản thân tôi là\r\n con người tuyệt vời. Tôi thấy yêu bản thân mình hơn, cảm thây vô cùng \r\nhạnh phúc bởi tôi đã là một con người mới. Cảm ơn thầy Hùng, cảm ơn BTC \r\nThaihabooks đã tổ chức buổi huân luyện ngày hôm nay đã giúp các học viên\r\n khác thay đổi tâm thức, thay đổi cuộc đời.”</p>\r\n<p align="right">\r\n	<strong>Nguyễn Thu Hà – Ngân hàng Quân Đội CN Hoàn Kiếm</strong></p>\r\n<p align="right">\r\n	&nbsp;</p>\r\n<p style="text-align: left;" align="center">\r\n	“ Sáng nay 2 chị em em đã không ngại mưa rét từ TP Thái Nguyên xuống HN\r\n bằng xe máy và quả thật 2 chị em đã may mắn rất nhiều. Khóa học rất bổ \r\ních và cần thiết cho giới trẻ, cho các bạn sinh viên Việt Nam. Em đã \r\nnhận ra cuộc sống không chỉ là biết nhận mà còn phải biết cho đi trước \r\nkhi muốn nhận.”</p>\r\n<p align="right">\r\n	<strong>Nguyễn Văn Thức – Sinh viên ĐH KTCN Thái Nguyên</strong></p>\r\n<p align="right">\r\n	&nbsp;</p>\r\n<p style="text-align: left;" align="center">\r\n	“Một khóa học tuyệt vời. Nó làm cho tôi quyết tâm trải nghiệm ở kiếp \r\nsống này. Tôi hiểu rằng tôi đến thế giới này để học hỏi, hoàn thành và \r\nyêu thương. Nó giúp tôi thể hiện được tình yêu của tôi dành cho bố mẹ \r\ntôi khi tôi mời hai người tham dự khóa học này. Tôi thấy rằng tôi còn \r\nrất giàu có và may mắn. Với tiềm năng sẵn có tôi sẽ làm được nhiều điều \r\nmà người khác cho là kỳ diệu.”</p>\r\n<p align="right">\r\n	<strong>Nguyễn Việt Anh – Công ty TNHH Thương Mại và Dịch Vụ Phú Thành</strong></p>\r\n<p align="right">\r\n	&nbsp;</p>\r\n<p style="text-align: left;" align="center">\r\n	“Em thực sự gửi lời cảm ơn tới Thầy và các thành viên trong buổi học \r\nngày hôm nay. Thực sự lúc đầu trước khi em đến với buổi trải nghiêm này \r\nem vẫn còn nghi ngờ vì đối với em là 1 sinh viên số tiền tham gia khóa \r\nhọc là không hề nhỏ nhưng bây giờ em thực sự thấy số tiền quá nhỏ bé so \r\nvới những gì em nhận được ngày hôm nay. Nó quả thực vô cùng to lớn, vô \r\ncùng tuyệt vời. Em đã khóc, khóc thực sự khi anh Hòa - học viên khiếm \r\nthị lên phát biều. Em khóc cho sự ngu muội của mình trước đây.Em yêu tất\r\n cả mọi người!”</p>\r\n<p align="right">\r\n	<strong>Nguyễn Thị Chi – SV ĐH Y Dược Thái Nguyên</strong></p>\r\n<p align="right">\r\n	&nbsp;</p>\r\n<p style="text-align: left;" align="center">\r\n	“ Rất tuyệt vời! Từ trước tới giờ tôi cũng đã tham gia nhiều khóa học \r\nnhưng chưa có khóa học nào truyền cho tôi nhiều động lực, nhiệt huyết \r\nnhư khóa học này. Thông qua khóa học tôi đã khẳng định rõ hơn Tôi là ai?\r\n Tôi như thế nào? Vâng! Tôi khỏe mạnh. Tôi tuyệt vời. Tôi thông minh. \r\nTôi thành công. Tôi là số 1. Tôi duy nhất. Cám ơn Thầy Hùng và BTC \r\nchương trình.”</p>\r\n<p align="right">\r\n	<strong>Nguyễn Đình Vinh – ĐH KHXH &amp; NV</strong></p>\r\n<p style="text-align: right;" align="right">\r\n	...............</p>\r\n<p align="center">\r\n	&nbsp;</p>\r\n<p align="center">\r\n	<strong>MẠNG CỘNG ĐỒNG SỰ KIỆN HAY</strong></p>\r\n<p align="center">\r\n	ADD: TÒA NHÀ SUKIENHAY, 20/176 LÊ TRỌNG TẤN, QUẬN THANH XUÂN, HÀ NỘI</p>\r\n<p align="center">\r\n	<span style="font-family: Arial; font-size: x-small;"><span style="font-size: small;">Tel: 04 3565 1974 * Fax: 04 6276 4656<br>\r\n	Email: info@sukienhay.com * Website: www.sukienhay.com</span></span></p>\r\n<p align="center">\r\n	&nbsp; <span style="font-family: Arial; font-size: x-small;"><span style="font-size: small;"><strong>Mong gặp lại bạn trong chương trình !</strong></span></span></p>			\r\n				</div>', 'Hà N?i', '552354db125de8539ce15be2.jpg', '2012-04-09 06:00:00', '2012-04-07 06:00:00', 12, 'lớn', '2012-04-07 11:22:36', '2012-04-07 11:22:42', 1, 1, 0, 0, 0, 0);
INSERT INTO `tbl_event` (`id`, `typeevent_id`, `name`, `content`, `place`, `thumbnail`, `start_time`, `end_time`, `number_guest`, `dimention`, `create_time`, `update_time`, `create_user_id`, `view`, `censor`, `slide_small`, `slide_lage`, `e_like`) VALUES
(2, 8, '[Ưu đãi lớn] Bí quyết trình bày từ diễn giả số 1 - Quách Tuấn Khanh', '<div class="ctitle">Thông tin sự kiện<b> [Ưu đãi lớn] Bí quyết trình bày từ diễn giả số 1 - Quách Tuấn Khanh</b></div>\r\n						<div class="deal_cont">\r\n	<div class="deal_cont_main">\r\n		<div class="deal_cont_main_left" id="leftcol">\r\n			<p>\r\n				<span style="font-size: small;"><img src="http://datve.sukienhay.com/images/icon/question-icon.png" style="float: left;" border="0" width="150"> Bạn muốn thành công và tạo dựng hình ảnh tích cực cho bản thân?</span><br>\r\n				<span style="font-size: small;">Bạn muốn mọi người dâng hiến trái tim cho bạn?</span><br>\r\n				<span style="font-size: small;">Bạn muốn truyền cảm hứng để mọi người phát huy hết tiềm năng bản thân? </span><br>\r\n				<span style="font-size: small;">Bạn muốn chia sẻ những ước mơ tuyệt vời nhất của mình cho mọi người cùng bạn theo đuổi và biến ước mơ thành hiện thực?</span><br>\r\n				<span style="font-size: small;">Bạn muốn trở thành người có sức hút giữa chốn đông người?</span><br>\r\n				<span style="font-size: small;">Bạn muốn thăng tiến nhanh chóng trong mọi công việc hay sự nghiệp đang theo đuổi?</span></p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p>\r\n				<strong><span style="font-size: small;">Công ty cổ phần đào tạo Nói Là Làm&nbsp; và Mạng cộng đồng Sự Kiện Hay gửi đến bạn chương trình</span></strong></p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size: small;"><span style="font-size: large; color: #ff6600;"><strong>"BÍ QUYẾT TRÌNH BÀY CỦA DIỄN GIẢ SỐ 1"</strong></span></span></p>\r\n			<p style="text-align: center;">\r\n				&nbsp;</p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size: small;"><span style="font-size: small;"><img alt="Quách Tuấn Khanh" src="http://datve.sukienhay.com/images/su-kien/hoi-thao/t3/quach-tuan-khanh-2204/trinhbay-quachtuankhanh.jpg" style="border-width: 0pt; border-style: solid; height: 313px; width: 530px;"></span></span></p>\r\n			<p>\r\n				<br>\r\n				<strong><span style="font-size: small;"><span style="font-size: small;"><img alt="micro icon" src="http://datve.sukienhay.com/images/icon/mic-icon.png" style="float: left;" title="micro icon" border="0" width="30"></span> </span></strong></p>\r\n			<p>\r\n				<strong><span style="font-size: small;">&nbsp;<span style="font-size: medium;">Nội dung chương trình:</span></span></strong></p>\r\n			<p>\r\n				<br>\r\n				<strong><span style="font-size: small;"><span style="font-size: small;"><img src="http://datve.sukienhay.com/images/icon/tick-icon.png" border="0" width="30"></span> Phân tích tâm lý thính giả và theo thính giả suốt bài trình bày</span></strong><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Các yếu tố để phân tích thính giả</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Bảy nhu cầu trong tiềm thức của người nghe</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Những rào cản khiến khán giả không lắng nghe bạn</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Ứng dụng nhữn điều mà truyền hình dạy ta</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Các đặc điểm chung của người nghe ngày nay</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Những câu hỏi thường xuyên lởn vởn trong đầu người nghe</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Đừng quên mục tiêu của người nghe: WIIFM?</span></p>\r\n			<p>\r\n				<br>\r\n				<span style="font-size: small;"><strong><span style="font-size: small;"><span style="font-size: small;"><img src="http://datve.sukienhay.com/images/icon/tick-icon.png" border="0" width="30"></span></span></strong> <strong>Soạn một bài trình bày thuyết phục</strong></span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Sáu bước soạn một bài trình bày hiệu quả</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Phác thảo bài trình bày</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Nghiên cứu tài liệu cho bài trình bày</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Chọn kiểu cấu trúc bài trình bày thuyết phục</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Phát triển các chi tiết</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Sử dụng các kỹ thuật thuyết phục như câu chuyện, số liệu, sự kiện, lịch sử…</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Sử dụng câu trích dẫn thật “đắt”</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Kết thúc hứng khởi và thúc giục hành động</span></p>\r\n			<p>\r\n				<br>\r\n				<span style="font-size: small;"><strong><span style="font-size: small;"><span style="font-size: small;"><img src="http://datve.sukienhay.com/images/icon/tick-icon.png" border="0" width="30"></span></span></strong> <strong>Chuẩn bị tâm lý</strong></span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Kiểm soát nỗi sợ</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Làm gì với đôi tay?</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Di chuyển trên sân khấu</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Làm sao tạo hào hứng khi bạn không phải là ca sĩ?</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Làm sao để mọi người không chán mình?</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Là MỘT người nghe</span></p>\r\n			<p>\r\n				<br>\r\n				<span style="font-size: small;"><strong><span style="font-size: small;"><span style="font-size: small;"><img src="http://datve.sukienhay.com/images/icon/tick-icon.png" border="0" width="30"></span></span>&nbsp;&nbsp; Kỹ thuật trình bày</strong></span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Dùng bản đồ trí não để trình bày tự nhiên</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;30 giây đầu tiên ấn tượng</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Các câu hỏi “làm nóng” người nghe</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Xây dựng đối thoai với người nghe</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Phương pháp lôi cuốn người nghe ngay từ đầu</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Các kỹ thuật làm người nghe “nhập cuộc” suốt lúc trình bày</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Tám cách để nhận phản hồi từ người nghe</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Điệu nghệ trong phần Hỏi – Đáp</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Làm gì khi…không ai hỏi?</span></p>\r\n			<p>\r\n				<br>\r\n				<strong><span style="font-size: small;"><span style="font-size: small;"><span style="font-size: small;"><img src="http://datve.sukienhay.com/images/icon/tick-icon.png" border="0" width="30"></span></span> Tuyệt chiêu để tạo hưng phấn và truyền cảm hứng</span></strong><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Tạo hình ảnh vầ phong cách riêng</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Truyền thông khơi khát vọng</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Sử dụng đồ vật minh hoạ</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Các kỹ thuật diễn thuyết và nhấn mạnh quan điểm</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Truyền nhiệt thuyết và hào hứng vào bài giảng</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Thuyết phục và lôi kéo người nghe</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Đừng để người nghe ngồi yên: vận động</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Cách khơi gợi cho mọi người tham gia các hoạt động</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Âm nhac, hình ảnh và video clip tạo hiệu ứng trực quan</span></p>\r\n			<p>\r\n				<br>\r\n				<strong><span style="font-size: small;"><span style="font-size: small;"><span style="font-size: small;"><img src="http://datve.sukienhay.com/images/icon/tick-icon.png" border="0" width="30"></span></span> Điệu nghệ trong giọng nói và ngôn ngữ cơ thể</span></strong></p>\r\n			<p>\r\n				<br>\r\n				<strong><span style="font-size: small;">* Giọng nói</span></strong><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Làm chủ giọng nói</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Im lặng tạo uy lực</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Biến thể giọng nói: sử dụng cường độ, nhịp điệu, trường độ, tông điệu</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Hướng dẫn biểu hiện cảm xúc cho bài nói</span><br>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; •&nbsp;&nbsp; &nbsp;Hướng dẫn tăng giảm tông giọng</span></p>\r\n			<p>\r\n				<strong><span style="font-size: small;">&nbsp;* Ngôn ngữ cơ thể</span></strong></p>\r\n			<p>\r\n				<span style="font-size: small;"><span style="font-size: small;">&nbsp; &nbsp; &nbsp;&nbsp; •&nbsp;&nbsp; </span>Ngôn ngữ cơ thể là ngôn ngữ của tiềm thức</span><span style="font-size: small;"><span style="font-size: small;">&nbsp; </span></span></p>\r\n			<p>\r\n				<span style="font-size: small;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; • </span>Dùng ngôn ngữ cơ thể minh hoạ cho nội dung</span></p>\r\n			<p>\r\n				<span style="font-size: small;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; • </span>Tạo ấn tượng và nhấn mạnh bằng ngôn ngữ cơ thể</span></p>\r\n			<p>\r\n				<span style="font-size: small;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; • </span>Cứ để mọi thứ tự nhiên</span></p>\r\n			<p>\r\n				<span style="font-size: medium;"><strong><img alt="mouse-icon" src="http://datve.sukienhay.com/images/icon/mouse-icon.jpg" style="float: left;" title="mouse-icon" border="0" height="38" width="29"></strong></span></p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p>\r\n				<span style="font-size: medium;"><strong>&nbsp;Mục tiêu chương trình:</strong></span></p>\r\n			<p>\r\n				<strong>Sau 2 buổi huấn luyện kèm hướng dẫn bí quyết, người tham dự sẽ gặt hái được:</strong><span style="font-size: small;">&nbsp;</span></p>\r\n			<p>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; • </span>Tự tin và chiến thắng nỗi sợ khi trình bày</p>\r\n			<p>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; • </span>Biết chuẩn bị một bài trình bày có sức thuyết phục, tạo được ấn tượng cho người nghe</p>\r\n			<p>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; • </span>Biết cách thu hút và lôi cuốn sự chú ý của người nghe trong lúc trình bày</p>\r\n			<p>\r\n				<span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; • </span>Lên kế hoạch rèn luyện những mặt còn yếu trong kỹ năng trình bày</p>\r\n			<p>\r\n				<strong>Thời gian: </strong>Từ 8h30 - 18h00, Chủ nhật ngày 22/04/2012</p>\r\n			<p>\r\n				<strong>Địa điểm:</strong> Hội trường Hoa Sen 6, Khách sạn Kim Liên, số 7 Đào Duy Anh, Đống Đa, Hà Nội</p>\r\n			<p>\r\n				<strong>Đối tượng tham dự:</strong> Lãnh đạo các cơ quan, người đi làm, sinh viên, học sinh</p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p>\r\n				Hãy nắm bắt các bí quyết từ <strong>diễn giả</strong> hàng đầu Việt Nam <strong>Quách Tuấn Khanh</strong>,\r\n để được xem những bài diễn thuyết hay nhất thế giới, và nắm bắt cách \r\nbiến mọi bài trình bày – diễn thuyết của mình thành thông điệp chinh \r\nphục lòng người.</p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p>\r\n				<span style="font-size: small;"><img alt="Quách Tuấn Khanh" src="http://datve.sukienhay.com/images/su-kien/hoi-thao/t3/quach-tuan-khanh-2204/quach_tuan_khanh-dien-gia.jpg" style="display: block; margin-left: auto; margin-right: auto; border-width: 0pt; border-style: solid; height: 368px; width: 550px;"></span></p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size: medium;"><strong>GIÁ VÉ THAM DỰ: <span style="text-decoration: line-through;">700.000</span> VNĐ</strong></span></p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size: medium;"><strong>DUY NHẤT, ĐĂNG KÝ TẠI SỰ KIỆN HAY GIÁ CHỈ: <span style="color: #ff0000;">280.000 VNĐ</span></strong></span></p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size: medium;">QUÝ KHÁCH ĐƯỢC TẶNG THÊM 01 VÉ THAM DỰ HỘI THẢO INTERNET MARKETING&nbsp; TRỊ GIÁ 100.000 VNĐ</span></p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size:16px;"><strong>Hotline: 0983 15 89 87/ 0948 11 44 71</strong></span></p>\r\n			<p style="text-align: center;">\r\n				&nbsp;<a href="http://datve.sukienhay.com/c/bi-quyet-lam-giau-tu-dien-gia-so-1-quach-tuan-khanh-14.html" target="_blank"><span style="font-size: small;"><strong><img alt="dangky-sukienhay" src="http://sukienhay.com/images/stories/logo/dangky-sukienhay.png" style="border: 0px none currentcolor;" title="dangky-sukienhay" border="0" height="75" width="233"></strong></span></a></p>\r\n			<p>\r\n				<span style="font-size: small;"><strong>&nbsp;Thông tin diễn giả:</strong></span></p>\r\n			<table border="0">\r\n				<tbody>\r\n					<tr>\r\n						<td>\r\n							<span style="font-size: small;"><img src="http://datve.sukienhay.com/images/su-kien/hoi-thao/t3/quach-tuan-khanh-2204/quach-tuan-khanh.jpg" style="margin: 5px; width: 156px; height: 187px;" border="0"></span></td>\r\n						<td>\r\n							<span style="font-size: small;">&nbsp;</span>\r\n							<p>\r\n								<span style="font-size: small;">Anh là một doanh nhân thành \r\ncông, diễn giả chuyên nghiệp và là chuyên gia về lĩnh vực Phát triển con\r\n người và Phát triển doanh nghiệp. Các đề tài anh huấn luyện: \r\nLeadership, Management, Sale, Corporate culture, Team-building, Work \r\nskills, Personal growth</span></p>\r\n							<p>\r\n								&nbsp;</p>\r\n							<p>\r\n								<span style="font-size: small;">Hiện anh là Chủ tịch Power UP \r\nGroup tiên phong trong lĩnh vực phát triển con người, diễn giả của Công \r\nty d’Oz International (Singapore) &amp; là 1 trong 6 PEP (Personal \r\nEfficiency Program) facilitator đầu tiên được chứng nhận quốc tế ở khu \r\nvực Châu Á. Ngoài bằng MBA của Trường Quản trị Maastricht, Hà Lan, anh \r\ncòn thu gặt được nhiều kinh nghiệm đa ngành sau hơn 20 năm làm việc \r\ntrong các lĩnh vực báo chí, quản lý PR &amp; Marketing, Sale, điều hành \r\ndoanh nghiệp và giảng dạy Đại học.</span></p>\r\n							<p>\r\n								&nbsp;</p>\r\n							<p>\r\n								<span style="font-size: small;">Anh đã giúp đánh thức và truyền \r\ncảm hứng cho hơn 100.000 người mỗi năm bao gồm sinh viên, quản lý, người\r\n hành nghề chuyên nghiệp, giám đốc, chủ doanh nghiệp... phát huy tiềm \r\nnăng để có thể đạt những thành quả xuất sắc trong nhiều lĩnh vực.</span></p>\r\n						</td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p>\r\n				<span style="font-size: small;"><span style="font-size: medium;"><strong>Nhận xét của những người tham dự</strong></span></span></p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p>\r\n				<em><strong>Chị Nguyễn Thị Kim Khánh - Giám Đốc Sale - Marketing Viettel telecom:</strong></em></p>\r\n			<p>\r\n				"Tôi rất ấn tượng về chương trình và phong các giảng dạy của diễn giả - dí dỏm và vô cùng sâu sắc"</p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p>\r\n				<em><strong>Bà Nguyễn Thị Phùng Thu - Giám đốc nhân sự LG Vina Cosmetic:</strong></em></p>\r\n			<p>\r\n				" Các chương trình của anh Khanh đều cuốn hút, không chỉ giảng dạy \r\nkỹ năng hiệu quả mà còn truyền "lửa" cho học viên - giúp họ có nghị lực \r\nđể dám thay đổi"</p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p>\r\n				<em><strong>Bà Dương Ngọc Dung - Tổng giám đốc Tổng công ty May Nhà Bè:</strong></em></p>\r\n			<p>\r\n				" Anh Khanh đúng là bậc thầy về khích lệ tinh thần và huấn luyện đội\r\n ngũ - sau mỗi chương trình lại cho tôi những nhận thức mới và cảm hứng \r\nđể tiếp tục hun đúc hoài bão xây dựng nề nếp công ty, lan truyền ước mơ,\r\n tương lai của công ty cho nhân viên."</p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p>\r\n				<em><strong>Chị Tô Thị Lan Hương - Trưởng phòng HC - NS Canon Viet Nam</strong></em></p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p>\r\n				"Quả thật đây là một món quà rất lớn cho nhân viên cấp cao của Canon\r\n và của riêng em - một người có rất ít thời gian để đọc sách và thư \r\ngiãn. Em đã được tham dự nhiều khóa học từ khi vào Canon. Tuy nhiên,&nbsp; \r\nkhóa học của anh Quách Tuấn Khanh đã tạo cho em những ấn tượng sâu sắc."</p>\r\n			<p>\r\n				<em><span style="font-size: medium;">....Và hàng chục nghìn người đã thay đổi cuộc sống hoàn toàn nhờ diễn giả số 1 - Quách Tuấn Khanh</span></em></p>\r\n			<p>\r\n				&nbsp;</p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size: medium;"><strong>GIÁ VÉ THAM DỰ: <span style="text-decoration: line-through;">700.000</span> VNĐ</strong></span></p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size: medium;"><strong>DUY NHẤT, ĐĂNG KÝ TẠI SỰ KIỆN HAY GIÁ CHỈ: <span style="color: #ff0000;">280.000 VNĐ</span></strong></span></p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size: medium;">QUÝ KHÁCH ĐƯỢC TẶNG THÊM 01 VÉ THAM DỰ HỘI THẢO INTERNET MARKETING&nbsp; TRỊ GIÁ 100.000 VNĐ</span></p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size:16px;"><strong>Hotline: 0983 15 89 87/ 0948 11 44 71</strong></span></p>\r\n			<p style="color: #cc0000;" align="center">\r\n				<a href="http://datve.sukienhay.com/c/bi-quyet-lam-giau-tu-dien-gia-so-1-quach-tuan-khanh-14.html" target="_blank"><span style="font-size: small;"><strong><img alt="dangky-sukienhay" src="http://sukienhay.com/images/stories/logo/dangky-sukienhay.png" style="border: 0px none currentcolor;" title="dangky-sukienhay" border="0" height="75" width="233"></strong></span></a></p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size: medium; color: #ff0000;"><strong>HÃY THAM DỰ ĐỂ ĐÓN NHẬN VÀ THAY ĐỔI HOÀN TOÀN CUỘC SỐNG CỦA BẠN</strong></span></p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size: medium; color: #ff0000;"><strong><span style="font-size: small;"><img alt="alt" src="http://datve.sukienhay.com/images/su-kien/hoi-thao/t3/quach-tuan-khanh-2204/changelife.jpg" style="border-width: 0pt; border-style: solid; height: 305px; width: 530px;"></span></strong></span></p>\r\n			<p style="text-align: center;">\r\n				&nbsp;</p>\r\n			<p style="text-align: center;">\r\n				<span style="font-size: small;"><strong>MẠNG CỘNG ĐỒNG SỰ KIỆN HAY - NÂNG TẦM GIÁ TRỊ VIỆT</strong></span></p>\r\n			<p style="text-align: center;">\r\n				&nbsp;Địa chỉ : Tòa nhà SuKienHay, Số 20/176 Lê Trọng Tấn, Thanh Xuân, Hà Nội</p>\r\n			<p style="text-align: center;">\r\n				&nbsp;Điện thoại: 04. 3565 1974 – Fax: 04. 6276 4656 – Email: <a href="mailto:info@sukienhay.com" title="Mail hỗ trợ - giải đáp thắc mắc">info@sukienhay.com</a></p>\r\n		</div>\r\n	</div>\r\n</div>', 'Hà N?i', '4859e1ac0d2316d3884599c3.jpg', '2012-04-07 06:00:00', '2012-04-07 06:00:00', 10000, 'lớn', '2012-04-07 11:23:49', '2012-04-07 11:44:07', 1, 6, 1, 0, 0, 0);
INSERT INTO `tbl_event` (`id`, `typeevent_id`, `name`, `content`, `place`, `thumbnail`, `start_time`, `end_time`, `number_guest`, `dimention`, `create_time`, `update_time`, `create_user_id`, `view`, `censor`, `slide_small`, `slide_lage`, `e_like`) VALUES
(3, 2, '[MUA 1 TẶNG 1] SMART CONGRESS - Học Internet marketing từ những chuyên gia hàng đầu thế giới', '<div class="event-desc">\r\n						<div class="ctitle">Thông tin sự kiện<b> [MUA 1 TẶNG 1] SMART CONGRESS - Học Internet marketing từ những chuyên gia hàng đầu thế giới</b></div>\r\n						<p>\r\n	<img alt="alt" src="http://smart.babylons.com.vn/images_smart/smart-congress-edmbanner.jpg" style="width: 554px; height: 185px;"></p>\r\n<p style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size: 16px;"><strong>1 VÉ DÀNH CHO 2 NGƯỜI THAM DỰ</strong></span></span></p>\r\n<p style="text-align: center;">\r\n	<strong>(Chỉ có ở Sự Kiện Hay)</strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n<div>\r\n	<strong><span class="heading1">“Đây là cơ hội để bạn tìm hiểu</span></strong><br>\r\n	<span class="style15">Làm thế nào để kinh doanh trên Internet với lợi nhuận cao,<br>\r\n	Kiếm tiền trên tại nhà trong chưa đầy 3 ngày hoặc nhanh hơn…</span><br>\r\n	<span class="heading1">Và tận hưởng một cuộc sống mà bạn mơ ước!”</span></div>\r\n<p class="subheading3">\r\n	Nếu bạn biết rằng ngoài kia có rất nhiều điều tốt đẹp đang chờ đợi mình...<br>\r\n	nếu bạn muốn cuộc sống đơn điệu này trở nên ý nghĩa hơn...<br>\r\n	nếu bạn muốn mỗi ngày thức dậy đều cảm thấy hào hứng và tràn đầy năng lượng...</p>\r\n<p class="basictext">\r\n	Nếu bạn muốn tận mắt chứng kiến từng bước những phương pháp mà các \r\nchuyện gia đã áp dụng để tạo ra thu nhập lên đến 6 con số mỗi tháng ngay\r\n tại nhà và học cách làm được như họ… thì hãy ĐẶT CHỖ NGAY BÂY GIỜ!</p>\r\n<p class="heading2 style16">\r\n	Hãy tưởng tượng cuộc sống của bạn sẽ tuyệt vời như thế nào nếu bạn có \r\nthể kiếm được số tiền, tương đương với thu nhập của một công việc toàn \r\nthời gian, là:</p>\r\n<p class="heading2 style16">\r\n	&nbsp;</p>\r\n<p class="heading1" align="center">\r\n	<span style="font-size:16px;"><strong><span class="style16">100.000 USD</span> mỗi năm ngay tại nhà?</strong></span></p>\r\n<p class="heading1" align="center">\r\n	&nbsp;</p>\r\n<table style="width: 502px; height: 509px;" align="center" border="0">\r\n	<tbody>\r\n		<tr>\r\n			<td class="subheading3" colspan="2">\r\n				<div align="center">\r\n					<p>\r\n						<strong>Đó là lý do tại sao bạn cần tham dự SMART Congress…bằng bất kỳ giá nào!</strong></p>\r\n				</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height="196" valign="top" width="44">\r\n				<img src="http://smart.babylons.com.vn/images_smart/GIS_NewDesign_ThumbIcon.gif"></td>\r\n			<td class="basictext" valign="top" width="620">\r\n				<div align="justify">\r\n					<strong>Chương trình hội thảo đặc biệt</strong> – Xây dựng nền tảng\r\n cơ bản ngay từ ngày đầu tiên, bạn sẽ được các chuyên gia giúp bạn lập \r\nkế hoạch và chiến lược phát triển công việc kinh doanh trực tuyến (không\r\n cần mang theo máy tính). Dù bạn là một người hoàn toàn chưa biết gì và \r\nđang muốn từng bước tiếp cận với lĩnh vực này hay một người đã dày dạn \r\nkinh nghiệm đến đây với mong muốn nâng cao thành công của bạn, thì đây \r\nsẽ là nơi cung cấp cho bạn những lối tư duy mới, những kỹ năng và công \r\ncụ hữu hiệu để có hướng đi đúng đắn nhất. Hơn thế nữa, chương trình sẽ \r\nđược giảng dạy với tốc độ hợp lý, sử dụng những thuật ngữ đơn giản, dễ \r\nhiểu để bạn có thể dễ dàng theo dõi. Khi bạn đã hoàn thành chương trình,\r\n bạn sẽ thực sự ngạc nhiên vì chính sự đơn giản của nó lại là bước đệm \r\nđể giúp bạn phát triển công việc kinh doanh trên Internet mang lại lợi \r\nnhuận đầu tiên!</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height="69" valign="top">\r\n				<img src="http://smart.babylons.com.vn/images_smart/GIS_NewDesign_ThumbIcon.gif"></td>\r\n			<td class="basictext" valign="top">\r\n				<div align="justify">\r\n					<strong>Thu được ROI (Lợi nhuận trên vốn đầu tư) nhanh hơn rất nhiều</strong> - Bạn sẽ được học làm thế nào để <strong>kinh doanh trực tuyến với vốn đầu tư tối thiểu</strong> trong khoảng thời gian ngắn nhất, nhờ đó bạn có thể làm việc ở bất cứ đâu và bất cứ khi nào bạn muốn.</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height="189" valign="top">\r\n				<img src="http://smart.babylons.com.vn/images_smart/GIS_NewDesign_ThumbIcon.gif"></td>\r\n			<td class="basictext" valign="top">\r\n				<div valign="top" align="justify">\r\n					<strong>Chương trình gặp gỡ và chia sẻ</strong> - Đây là cơ hội \r\nvàng để bạn gặp gỡ với những người đã rất thành công trong lĩnh vực kinh\r\n doanh trực tuyến. Bạn sẽ có thể trưc tiếp gặp gỡ và trò chuyện với các \r\nchuyên gia hàng đầu trong lĩnh vực này, và tìm hiểu <strong>bí quyết để tạo ra 1 triệu đô đầu tiên trên Internet</strong>\r\n của họ! Các chuyên gia sẽ làm thế nào để giúp bạn nhanh chóng thành \r\ncông trong kinh doanh trên Internet? Bạn sẽ được học hỏi từ những người \r\ndày dạn kinh nghiệm, “đã làm và làm rất tốt ", và đó không phải những \r\nmọt sách đọc hàng tá lý thuyết rồi nghĩ rằng lý thuyết đó sẽ giúp bạn \r\nkiếm tiền. Bạn sẽ được học từ các chuyên gia THỰC SỰ đang ở đỉnh cao sự \r\nnghiệp của họ và đang sử dụng những <strong>công cụ</strong> tuyệt vời để kiếm hàng triệu đô trên Internet.</div>\r\n				<div valign="top" align="justify">\r\n					<p style="text-align: center;">\r\n						<img alt="alt" src="http://sukienhay.com/images/stories/sukien/2012/t3/smart.jpg" style="width: 493px; height: 195px;"></p>\r\n					<p style="text-align: center;">\r\n						<strong><span style="font-size: 18px;"><span style="color:#ff0000;">ĐĂNG KÝ NGAY</span>, GỌI SỐ: </span></strong><strong><span style="font-size:18px;">0948 114 471 / 0983 15 8987</span></strong></p>\r\n					<p style="text-align: center;">\r\n						<span style="font-size:16px;"><strong>GIÁ VÉ: <strike>2.048.000</strike></strong></span></p>\r\n					<p style="text-align: center;">\r\n						<span style="color:#ff0000;"><span style="font-size: 18px;"><strong><a href="http://datve.sukienhay.com/c/mua-1-tang-1-smart-congress-hoc-internet-marketing-tu-nhung-chuyen-gia-hang-dau-the-gioi-9.html?sideDealFlag=1" target="_blank">ĐĂNG KÝ TẠI SỰ KIỆN HAY</a>: 600.000 VNĐ</strong></span></span></p>\r\n					<p style="text-align: center;">\r\n						<span style="color: rgb(255, 0, 0);"><span style="font-size: 18px;"><strong>1 VÉ DÀNH CHO 2 NGƯỜI THAM DỰ</strong></span></span></p>\r\n					<p style="text-align: center;">\r\n						&nbsp;</p>\r\n					<p style="text-align: center;">\r\n						<strong>ĐẶC BIỆT ĐĂNG KÝ TẠI SUKIENHAY.COM ĐƯỢC TẶNG NGAY 1 VÉ THAM DỰ <a href="http://sukienhay.com/OFFLINE-CASHFLOW-CLUB-CACH-CHIEN-THANG-TRONG-TRO-CHOI-TIEN-BAC-4012-2012.html" target="_blank">"OFFLINE CASHFLOW CLUB - CHIẾN THẮNG TRONG TRÒ CHƠI TIỀN BẠC"</a></strong></p>\r\n					<p style="text-align: center;">\r\n						<span style="font-size: medium;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong><a href="http://datve.sukienhay.com/c/mua-1-tang-1-smart-congress-hoc-internet-marketing-tu-nhung-chuyen-gia-hang-dau-the-gioi-9.html?sideDealFlag=1" target="_blank"><span style="font-size: small;"><strong><img alt="dangky-sukienhay" src="http://sukienhay.com/images/stories/logo/dangky-sukienhay.png" style="border: 0px none currentcolor; display: block; margin-left: auto; margin-right: auto;" title="dangky-sukienhay" border="0" height="75" width="233"></strong></span></a></strong></span></span></span></strong></span></p>\r\n				</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table class="heading3" style="width: 513px; height: 2506px;" border="0" cellpadding="0" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td align="center">\r\n				<p>\r\n					<img src="http://smart.babylons.com.vn/images_smart/thong-tin-dien-gia.jpg" style="height: 54px; width: 494px;"></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p>\r\n					<img src="http://smart.babylons.com.vn/images_smart/Jaz-Lai-La-Ai.jpg"></p>\r\n				<p>\r\n					<span class="subheading3"><strong>Jaz Lai – Siêu Sao Internet Phát Triển Nhanh Nhất</strong><br>\r\n					Phương pháp trở nên giàu có trên Internet của những “người lười” - \r\n“Bí Quyết tạo dòng Tiền từ Hộp Thư Đến – Làm sao để kiếm 500 – 800 đô/ \r\nngày chỉ bằng bấm nút đơn giản!”</span></p>\r\n				<img src="http://smart.babylons.com.vn/images_smart/Speaker_Jaz.jpg" style="width: 246px; height: 170px; border-width: 0pt; border-style: solid; float: left; margin: 5px;">\r\n				<p class="basictext" align="justify">\r\n					Jaz Lai, không giống như những Bậc Thầy Về Internet mà bạn vẫn \r\nthường thấy hiện nay. Những ấn tượng đầu tiên bạn có thể thấy về anh ấy \r\nlà một trang trai bình thường đang đi dạo thư thái với một dáng vẻ bảnh \r\nbao, quần bò mài thể thao và giày vải.</p>\r\n				<p class="basictext" align="justify">\r\n					<br>\r\n					Không ai có thể nghĩ rằng anh ấy trước đây từng là một nhà tạo mẫu \r\nchính hiệu – đã sở hữu một chuỗi cửa hàng bán lẻ, những giờ được biết \r\nđến như là một trong những bậc thầy hàng đầu trong lĩnh vực Internet \r\nMarketing trên thế giới. Jaz thực sự là một ví dụ hoàn hảo về những \r\nngười đầu tư thời gian rảnh rỗi và thành công trong một lĩnh vực mà anh \r\nchưa có một hiểu biết gì về nó.</p>\r\n				<p class="basictext" align="justify">\r\n					<br>\r\n					Giờ đây, với 7 năm kinh nghiệm làm Internet Marketing, Jaz đã tổng \r\nkết một chiến lược Đã Được Kiểm Chứng tạo ra thu nhập 5 – 6 con số vào \r\nhòm mail của bất cứ ai trong một khoảng thời gian ngắn. Và việc đó đơn \r\ngiản gần như việc bấm vào một nút trên máy tính.</p>\r\n				<p class="basictext" align="justify">\r\n					<br>\r\n					Với khả năng biến những phương pháp cổ điển thành những phương pháp\r\n tiếp cận Internet Marketing dễ hiểu và trực tiếp, Jaz luôn làm kinh \r\nngạc và truyền cảm hứng cho những học viên của mình.</p>\r\n				<p class="basictext" align="justify">\r\n					<br>\r\n					Phương pháp dạy trực tiếp và chứng kiến tận mắt những quy tắc \r\nInternet Marketing thành công của Jaz đã đến được với hàng trăm, hàng \r\nngàn học viên trên toàn thế giới. Họ đã được thuyết phục bởi những bài \r\ngiảng và phương pháp đơn giản của anh trong việc tạo ra Nguồn Doanh Thu \r\nTổng Hợp thông qua Internet.</p>\r\n				<p class="basictext" align="justify">\r\n					<br>\r\n					Phần lớn những người trong số này đã bỏ công việc mà họ vẫn làm để dành toàn bộ thời gian kinh doanh trên Internet.</p>\r\n				<p class="basictext" align="justify">\r\n					<br>\r\n					Jaz Lai hiện nay được coi là một trong những ngôi sao Châu Á thành công nhất trong lĩnh vực Internet Marketing.</p>\r\n				<p class="basictext" align="justify">\r\n					&nbsp;</p>\r\n				<p class="basictext">\r\n					<strong>Tại SMART Congress, Jaz sẽ chia sẻ với các bạn:</strong></p>\r\n				<ul><li class="basictext">\r\n						Phương pháp “one-article” (một bài viết) giúp đem lại 500 lượt khách ghé thăm trang web của bạn gần như Ngay Lập Tức</li><li class="basictext">\r\n						Lý do tại sao hầu hết mọi người không thể kiếm tiền được từ email marketing</li><li class="basictext">\r\n						Làm thế nào để kiếm được $500 - $800 một ngày, sử dụng một chiến lược đơn giản</li><li class="basictext">\r\n						Hãy làm “điều này” và thành công của bạn sẽ được ĐẢM BẢO</li><li class="basictext">\r\n						Một cách chắc chắn để xây dựng danh sách 5000 người chỉ với 30 phút mỗi ngày</li><li class="basictext">\r\n						Ví dụ “sống” về việc tôi kiếm tiền mỗi ngày như thế nào.</li></ul>\r\n				<p>\r\n					<img src="http://smart.babylons.com.vn/images_smart/Ken-Sapp.jpg"></p>\r\n				<p>\r\n					<strong><span class="subheading3">Ken Sapp – Nhà Chiến Lược trên Twitter, Chuyên Gia Internet Marketing, Chuyên Gia Hướng dẫn Thế hệ trẻ.</span></strong></p>\r\n				<img alt="Ken Sapp" src="http://smart.babylons.com.vn/images_smart/Speaker_ken.jpg" style="border-width: 0pt; border-style: solid; float: left; margin: 5px;">\r\n				<p class="basictext" align="justify">\r\n					Vài năm trước đây, do gánh nặng tài chính tăng lên, Ken phải từ bỏ \r\nđam mê của mình. Ông gác lại giấc mơ giúp đỡ những người trẻ tuổi và bắt\r\n đầu bỏ phí cuộc đời mình bằng cách bắt đầu một công việc làm thuê, sông\r\n nhờ vào đồng lương từ ông chủ. Sau đó, khi ông có thời gian và sức lực,\r\n thứ mà không phải lúc nào cũng có, ông đã làm những gì mà ông đam mê – \r\ngiúp đỡ giới trẻ khám phá và theo đuổi giấc mơ của họ.</p>\r\n				<p class="basictext" align="justify">\r\n					<br>\r\n					Sau đó, khi tham gia một số sự kiện, ông nhận ra bản thân mình \r\ntrong một hội thảo – sự kiện đó đã gieo trong đầu ông một ý nghĩ rằng \r\nông có thể kiếm tiền bằng cách làm những điều ông thực sự đam mê thay vì\r\n lãng phí cuộc đời mình trong một công việc làm thuê. Ông đã học một số \r\nchiến lược đơn giản giúp ông biến niềm đam mê của mình thành một công \r\nviệc kinh doanh trực tuyến có thể tạo ra 3000 USD một tháng một cách tự \r\nđộng. Giờ đây, ông không chỉ dành thời gian làm những điều ông đam mê, \r\nmà ông còn muốn giúp BẠN theo đuổi giấc mơ của mình và làm những điều \r\nbạn thực sự yêu thích và đam mê – biến nó thành những công việc kinh \r\ndoanh trực tuyến đem lại tiền cho bạn.</p>\r\n				<p class="basictext" align="justify">\r\n					<br>\r\n					Ken hiện đang là Phó Chủ Tích phụ trách Nội dung của SkyQuestCom và\r\n chịu trách nhiệm về website của công ty bên cạnh công việc kinh doanh \r\nqua internet của cá nhân ông.</p>\r\n				<p>\r\n					<img src="http://smart.babylons.com.vn/images_smart/Pamela-Siow.jpg"></p>\r\n				<p>\r\n					<strong><span class="subheading3">Pamela Siow – Chuyên gia tạo mối khách hàng</span></strong></p>\r\n				<img alt="Pamela Siow" src="http://smart.babylons.com.vn/images_smart/Speaker_pamela.jpg" style="border-width: 0pt; border-style: solid; float: left; margin: 5px;">\r\n				<p class="basictext" align="justify">\r\n					Pam Siow là một chuyên gia tạo mối khách hàng và hướng dẫn viên \r\nInternet Marketing. Cô yêu thích việc chia sẻ kiến thức của mình về \r\nInternet Marketing và quy trình để bắt đầu thiết lập công việc kinh \r\ndoanh trực tuyến chỉ bằng vài thao tác đơn giản Pam Siow sinh ra và lớn \r\nlên là một nữ doanh nhân điển hình đã làm việc tại rất nhiều công ty đa \r\nquốc gia. Suy nghĩ trở thành “chủ sở hữu của một doanh nghiệp” chưa bao \r\ngiờ xuất hiện trong tâm trí cô cho tới khi khoản nợ tích lũy trong thẻ \r\ntín dụng của Pam lên đến $ 14.000 khi cô mới 28 tuổi. Trong suốt thời \r\nđiểm đó, Pam cảm thấy cô cần phải hành động nhanh chóng để thay đổi cuộc\r\n sống khủng hoảng hiện tại. Bắt đầu công việc kinh doanh trên Internet \r\nđầu tiên của mình năm 28 tuổi mà không có bất kì sản phẩm hay dịch vụ \r\nnào trong tay bằng cách hợp tác với các chủ doanh nghiệp đang tìm cách \r\nquảng bá thương hiệu của họ lên Internet. Chỉ trong vòng 1 năm, Pam \r\nkhông những đã xóa sạch số nợ trong thẻ tín dụng của mình, mà còn kiếm \r\nđược 10.000 USD mỗi tháng với công việc kinh doanh trực tuyến.</p>\r\n				<p class="basictext" align="justify">\r\n					<br>\r\n					Ngày hôm nay, Pam là chủ sở hữu của chuỗi các trang web âm nhạc lớn\r\n nhất mang lại nguồn thu nhập thu động cho cô. Với kỹ năng kiếm tiền mới\r\n được biết đến và khả năng đơn giản hóa kiến thức chuyên ngành, Pam đã \r\ntrở thành một chuyên gia có uy tín trong việc dạy mọi người cách làm thế\r\n nào để có thể bắt đầu công việc kinh doanh riêng của họ với số vốn chỉ \r\nlà $ 150.</p>\r\n				<p class="basictext" align="justify">\r\n					<br>\r\n					Năng lực của cô đã vượt xa hơn lĩnh vực kinh doanh trực tuyến và \r\nchuyển sang tư vấn chiến lược marketing và thành công cho các doanh \r\nnhân. Pam đã phát triển công việc huấn luyện cho các doanh nghiệp và \r\nkiếm một triệu đô la trong vòng một năm. Để đạt được sự tăng trưởng vượt\r\n bậc như vậy, cô chủ yếu sử dụng các chiến lược marketing với chi phí \r\nthấp như sử dụng các phương tiện truyền thông xã hội để xây dựng danh \r\nsách khách hàng cho mình. Với niềm tin mạnh mẽ vào việc xây dựng giá trị\r\n cho người hâm mộ cô trên facebook, Pam đã thành công và rất nhiều người\r\n trong số đó đã trở thành khách hàng thân thiết của cô.</p>\r\n				<p class="basictext">\r\n					<strong>Cô chuyên giúp các cá nhân với kỹ thuật thấp phát triển \r\nkinh doanh trực tuyến của họ trong một khoảng thời gian ngắn, và dưới \r\nđây là một vài trong số những câu chuyện thành công trong vòng một năm:</strong></p>\r\n				<p>\r\n					&nbsp;</p>\r\n				<ul><li class="basictext">\r\n						Vanny, một kế toán đã phát triển công việc kinh doanh của mình và kiếm được $ 30.000 chỉ trong vòng 3 ngày</li><li class="basictext">\r\n						Dennis, Tổng giám đốc của một doanh nghiệp đã tạo ra doanh thu $ 500.000 trong vòng một năm</li><li class="basictext">\r\n						Wilson, một cựu kỹ sư đã kiếm được gần $ 70.000 mỗi năm với công \r\nviệc kinh doanh trực tuyến đầu tiên của mình và đã có thể ngừng làm việc\r\n trong vòng một năm</li><li class="basictext">\r\n						Chris, giành hợp đồng tư vấn đầu tiên của ông trị giá $ 9.000 trong vòng 2 tháng.</li></ul>\r\n				<p class="basictext">\r\n					&nbsp;</p>\r\n				<p class="basictext">\r\n					Pam Siow đã được phỏng vấn trên nhiều tờ báo, tạp chí, sách về các \r\nchủ đề internet marketing. Cô đã được mời làm diễn giả cho nhiều hiệp \r\nhội, các trường đại học, các chuyên gia khác nhau để chia sẻ những hiểu \r\nbiết về việc bắt đầu công việc kinh doanh trực tuyến.Cô cũng được vinh \r\ndanh là một trong những "nữ doanh nhân hàng đầu cho hiệu quả kinh doanh"\r\n trong Hội nghị thượng đỉnh các Nữ doanh nhân Quốc tế được tổ chức tại \r\nShine, Mỹ.</p>\r\n				<p class="basictext">\r\n					&nbsp;</p>\r\n				<p class="basictext">\r\n					<strong>Tại SMART Congress, Pam sẽ chia sẻ với bạn những phương pháp và công cụ hữu hiệu về:</strong></p>\r\n				<ul><li class="basictext">\r\n						Làm thế nào để bắt đầu kinh doanh với chưa đầy 300 đô Mỹ (thậm chí bạn có thể bắt đầu với con số, nếu bạn muốn!)</li><li class="basictext">\r\n						3 phương pháp đáng tin cậy để bạn bắt đầu kinh doanh trực tuyến... ngay từ hôm nay</li><li class="basictext">\r\n						3 sai lầm nguy hiểm nhất khi bắt đầu kinh doanh trực tuyến sẽ đè \r\nbẹp cơ hội thành công của bạn ... và làm thế nào BẠN có thể hoàn toàn \r\ntránh được những sai lầm đó khi bạn học những phương pháp tiếp cận đã \r\nđược Pam chứng minh</li><li class="basictext">\r\n						Làm thế nào để xác định các “ngách trực tuyến” có lợi nhuận cao \r\nnhất, từ đó bạn có thể bắt đầu tạo ra thu nhập ngay lập tức (ngay cả khi\r\n bạn không có một sản phẩm hoặc dịch vụ nào để bán)</li><li class="basictext">\r\n						3 bước đơn giản để nhân đôi và nhân ba thu nhập trực tuyến của bạn\r\n (các bước này rất dễ khi xem qua ... nhưng chúng thậm chí còn rất dễ \r\nlàm một khi tôi cho bạn thấy cách làm thế nào!)</li></ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p style="text-align: center;">\r\n	<img alt="alt" src="http://sukienhay.com/images/stories/sukien/2012/t3/smart.jpg" style="width: 493px; height: 195px;"></p>\r\n<p style="text-align: center;">\r\n	<strong><span style="font-size:20px;"><span style="color: rgb(255, 0, 0);">ĐĂNG KÝ NGAY</span>, GỌI SỐ: 0948 114 471/ 0983 15 8987</span></strong></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size:16px;"><strong>GIÁ VÉ: <strike>2.048.000</strike></strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size: 18px;"><strong>ĐĂNG KÝ TẠI SỰ KIỆN HAY: 600.000 VNĐ</strong></span></span></p>\r\n<p style="text-align: center;">\r\n	<br>\r\n	<span style="font-size: medium;"><strong><span style="font-size: small;"><span style="font-size: medium;"><span style="font-size: 16px;"><strong><a href="http://datve.sukienhay.com/c/mua-1-tang-1-smart-congress-hoc-internet-marketing-tu-nhung-chuyen-gia-hang-dau-the-gioi-9.html?sideDealFlag=1" target="_blank"><span style="font-size: small;"><strong><img alt="dangky-sukienhay" src="http://sukienhay.com/images/stories/logo/dangky-sukienhay.png" style="border: 0px none currentcolor; display: block; margin-left: auto; margin-right: auto;" title="dangky-sukienhay" border="0" height="75" width="233"></strong></span></a></strong></span></span></span></strong></span></p>\r\n<p style="text-align: center;">\r\n	<strong>ĐẶC BIỆT ĐĂNG KÝ TẠI SUKIENHAY.COM ĐƯỢC TẶNG NGAY 1 VÉ THAM DỰ&nbsp;</strong><a href="http://sukienhay.com/HOI-THAO-CHIEN-LUOC-XAY-DUNG-HE-THONG-MANG-XA-HOI-3956-2012.html" target="_blank"> "OFFLINE CASHFLOW CLUB - CHIẾN THẮNG TRONG TRÒ CHƠI TIỀN BẠC"</a></p>\r\n<table style="width: 520px; height: 1377px;" border="0" cellpadding="0" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td align="center">\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center">\r\n				<img src="http://smart.babylons.com.vn/images_smart/thong-tin-chuong-trinh.jpg" style="width: 471px; height: 51px;"></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center">\r\n				<table style="width: 573px; height: 1204px;" align="center" border="0" cellpadding="0" cellspacing="4">\r\n					<tbody>\r\n						<tr>\r\n							<td style="width:113px;height:57px;">\r\n								<p align="center">\r\n									<strong>ĐỊA ĐIỂM</strong></p>\r\n							</td>\r\n							<td style="width:528px;height:57px;">\r\n								<table style="width: 266px; height: 25px;" border="0" cellpadding="0" cellspacing="8">\r\n									<tbody>\r\n										<tr>\r\n											<td style="height:20px;">\r\n												<p>\r\n													<strong>Hà</strong><strong> Nội, Việt Nam</strong></p>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n						<tr>\r\n							<td style="height:39px;">\r\n								<p align="center">\r\n									<strong>THỜI GIAN </strong></p>\r\n							</td>\r\n							<td style="height:39px;">\r\n								<table style="width: 275px; height: 84px;" border="0" cellpadding="0" cellspacing="8">\r\n									<tbody>\r\n										<tr>\r\n											<td style="height:20px;">\r\n												<p>\r\n													<strong>Thứ</strong><strong> 7, ngày 7 tháng 4, 2012</strong></p>\r\n												<p>\r\n													<strong>&nbsp;Chủ nhật, ngày 8 Tháng 4, 2012</strong></p>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n						<tr>\r\n							<td style="height:145px;">\r\n								<p align="center">\r\n									<strong>ĐĂNG KÝ THAM D</strong></p>\r\n							</td>\r\n							<td style="height:145px;">\r\n								<table style="width: 428px; height: 276px;" border="0" cellpadding="0" cellspacing="8">\r\n									<tbody>\r\n										<tr>\r\n											<td style="height:111px;">\r\n												<p>\r\n													<strong>Thời</strong><strong> gian đăng kí: trước giờ học 1 tiếng</strong></p>\r\n												<p>\r\n													Mỗi học viêc đều PHẢI đăng kí. Chương trình dự kiến bắt đầu\r\n vào lúc 09h00 sáng. Quý khách hãy đến sớm để làm thủ tục đăng kí và có \r\nchỗ ngồi tốt.</p>\r\n												<p>\r\n													Quy trình vào cửa sẽ được kiểm soát nghiêm ngặt và quý khách cần xuất trình vé tại bàn đăng kí<strong>.</strong></p>\r\n												<p>\r\n													Vòng đeo tay sẽ được phát tại bàn đăng kí và đây là điều \r\nkiện bắt buộc để được vào hội trường. (*Vui lòng giữ vòng đeo tay trong \r\nsuốt 3 ngày. Trong trường hợp mất vòng đeo tay, vui lòng nộp 500.000VND \r\nđể được cấp mới.)</p>\r\n												<p>\r\n													Vé tham dự có thể chuyển nhượng sang cho người khác nhưng \r\nphải có văn bản thông báo cho ban tổ chức 14 ngày trước khi khóa học \r\ndiễn ra.</p>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n						<tr>\r\n							<td style="height:93px;">\r\n								<p align="center">\r\n									<strong>TRANG PHỤC</strong></p>\r\n							</td>\r\n							<td style="height:93px;">\r\n								<table style="width: 425px; height: 82px;" border="0" cellpadding="0" cellspacing="8">\r\n									<tbody>\r\n										<tr>\r\n											<td style="height:43px;">\r\n												<p>\r\n													Trang phục lịch sự và thoải mái. Hãy mang theo áo khoác vì nhiệt độ trong phòng có thể giảm.</p>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n						<tr>\r\n							<td style="height:34px;">\r\n								<p align="center">\r\n									<strong>THỨ CẤN MANG THEO</strong></p>\r\n							</td>\r\n							<td style="height:34px;">\r\n								<table style="width: 429px; height: 140px;" border="0" cellpadding="0" cellspacing="8">\r\n									<tbody>\r\n										<tr>\r\n											<td style="height:20px;">\r\n												<ul><li>\r\n														Bút và giấy viết&nbsp;</li><li>\r\n														Áo ấm. Nhiệt độ trong phòng có thể lạnh</li><li>\r\n														Giấy tờ đi lại (cho học viên người nước ngoài)&nbsp;</li><li>\r\n														Đồ ăn nhẹ</li><li>\r\n														Nước uống (Phải trong chai đóng nắp)</li><li>\r\n														Danh thiếp – để giao lưu kết nối</li></ul>\r\n												<p style="margin-left:.25in;">\r\n													LƯU Ý: Quý khách không cần mang theo máy tính.</p>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n						<tr>\r\n							<td style="height:93px;">\r\n								<p align="center">\r\n									<strong>QUAY PHIM</strong></p>\r\n								<p align="center">\r\n									<strong>GHI ÂM</strong></p>\r\n							</td>\r\n							<td style="height:93px;">\r\n								<table style="width: 425px; height: 49px;" border="0" cellpadding="0" cellspacing="8">\r\n									<tbody>\r\n										<tr>\r\n											<td style="height:41px;">\r\n												<p>\r\n													<strong>Không</strong><strong> được phép quay phim và ghi âm trong suốt thời gian diễn ra chương trình</strong></p>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n						<tr>\r\n							<td style="height:116px;">\r\n								<p align="center">\r\n									<strong>ĂN UỐNG</strong></p>\r\n							</td>\r\n							<td style="height:116px;">\r\n								<table style="width: 425px; height: 104px;" border="0" cellpadding="0" cellspacing="8">\r\n									<tbody>\r\n										<tr>\r\n											<td style="height:98px;">\r\n												<p>\r\n													Sẽ có thời gian nghỉ ăn trưa và ăn tối.</p>\r\n												<p>\r\n													Các diễn giả cam kết sẽ truyền tải toàn bộ nội dung khóa \r\nhọc với chất lượng cao nhất. Do đó, thời gian nghỉ có thể thay đổi tùy \r\nthuộc vào diễn giả. Quý khách nên mang theo đồ ăn nhẹ hoặc mua đồ ăn tại\r\n các cửa hàng xung quanh địa điểm học.</p>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n						<tr>\r\n							<td style="height:58px;">\r\n								<p align="center">\r\n									<strong>KHÁCH SẠN</strong></p>\r\n							</td>\r\n							<td style="height:58px;">\r\n								<table style="width: 418px; height: 34px;" border="0" cellpadding="0" cellspacing="8">\r\n									<tbody>\r\n										<tr>\r\n											<td style="height:43px;">\r\n												<p>\r\n													Sẽ được thông báo sau</p>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n						<tr>\r\n							<td style="height:58px;">\r\n								<p align="center">\r\n									<strong>ĐI LẠI</strong></p>\r\n							</td>\r\n							<td style="height:58px;">\r\n								<table style="width: 420px; height: 28px;" border="0" cellpadding="0" cellspacing="8">\r\n									<tbody>\r\n										<tr>\r\n											<td style="height:41px;">\r\n												<p>\r\n													Sẽ được thông báo sau</p>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n						<tr>\r\n							<td style="height:39px;">\r\n								<p align="center">\r\n									<strong>ĐỖ XE</strong></p>\r\n							</td>\r\n							<td style="height:39px;">\r\n								<table style="width: 421px; height: 36px;" border="0" cellpadding="0" cellspacing="8">\r\n									<tbody>\r\n										<tr>\r\n											<td style="height:20px;">\r\n												<p>\r\n													Đỗ xe theo yêu cầu của hội trường.</p>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p style="text-align: center;">\r\n	<br>\r\n	<strong>MẠNG CỘNG ĐỒNG SỰ KIỆN HAY </strong></p>\r\n<p style="text-align: center;">\r\n	Địa chỉ : Tòa nhà SuKienHay, Số 20/176 Lê Trọng Tấn, Thanh Xuân, Hà Nội</p>\r\n<p style="text-align: center;">\r\n	Điện thoại: <strong>04. 3565 1974</strong> – Fax: <strong>04. 6276 4656</strong> – Email: <a href="mailto:info@sukienhay.com" title="Mail hỗ trợ - giải đáp thắc mắc">info@sukienhay.com</a></p>			\r\n				</div>', 'Hà N?i', 'da96d119df4ff3ed182abdeb.jpg', '2012-04-07 06:00:00', '2012-04-07 06:00:00', 10000, 'lớn', '2012-04-07 11:24:54', '2012-04-07 11:25:01', 1, 1, 0, 0, 0, 0),
(4, 3, 'HỘI THẢO “XÂY DỰNG HỆ THỐNG MARKETING VỚI MXH”', '<div class="event-desc">\r\n						<div class="ctitle">Thông tin sự kiện<b> HỘI THẢO “XÂY DỰNG HỆ THỐNG MARKETING VỚI MXH”</b></div>\r\n						<p>\r\n	Bạn thân mến,</p>\r\n<p>\r\n	<strong><span style="background-color:#ffff00;">Sau chương trình Hội \r\nthảo Social Media ngày 31/3/2012 được sự quan tâm rất lớn của các khách \r\nhàng. Để đáp ứng lời đề nghị của nhiều khách hàng khác chưa tham dự được\r\n chúng tôi tổ chức thêm 01 buổi nữa để mang tới cho các bạn những kiến \r\nthức tuyệt vời từ Mạng xã hội.</span></strong></p>\r\n<h3 style="color:blue;">\r\n	<a href="https://plus.google.com/photos/106242674117334147484/albums/5726014289665283425?banner=pwa" target="_blank"><strong>Hình ảnh Hội thảo ngày 31/3/2012</strong>. <strong>Bạn có thể xem tại đây &gt;&gt;&gt;</strong></a></h3>\r\n<p>\r\n	<span style="color:#ff0000;"><em>Bạn đang mong muốn tìm kiếm phương pháp xây dựng hệ thống marketing và thương hiệu cho sản phẩm cũng như dịch vụ của bạn?</em></span></p>\r\n<p>\r\n	<br>\r\n	<span style="color:#ff0000;"><em>Bạn đang mong muốn chuyển đổi một cuộc sống mà bạn mong muốn từ Internet?</em></span></p>\r\n<p>\r\n	<br>\r\n	<strong>Và đây là một cơ hội rất tuyệt vời để bạn lựa chọn.</strong></p>\r\n<p>\r\n	<br>\r\n	Cùng với sự phát triển nhanh chóng của Internet tại Việt Nam. Social \r\nMedia là một xu hướng Marketing mới được xem là mới mẻ và đang có sức \r\nlan tỏa vô cùng mạnh mẽ. Hình thức của Social Media là những sản phẩm \r\ntruyền thông (tin, bài, hình ảnh, video clips…) do người dùng tạo ra và \r\nxuất bản trên Internet thông qua các mạng xã hội hay các diễn đàn. <strong>Với nguồn lực vô cùng to lớn từ Mạng xã hội, </strong>các Doanh nghiệp Việt Nam hiện tại cũng đã bắt đầu triển khai các kế hoạch Social Media cho hoạt động kinh doanh của mình.</p>\r\n<p>\r\n	<br>\r\n	<strong>Social Media </strong>cũng giúp mỗi cá nhân đều có thể trở \r\nthành nhà báo, đều có quyền tranh luận, phản biện, cung cấp thông tin \r\ntrước bất cứ một vấn đề của xã hội, hoặc có thể trở thành Đại sứ của đất\r\n nước mình thông qua các mạng xã hội... Thế giới hiện nay đang dần hình \r\nthành những biên giới số, ở đó mỗi cá nhân đều có cơ hội trở thành một \r\ncông dân mạng, và trong tương lai, biên giới số giữa những mạng xã hội \r\ncũng sẽ biến mất, để mỗi công dân trên Internet đều trở thành công dân \r\ntoàn cầu.</p>\r\n<p>\r\n	<br>\r\n	<strong>Để giúp bạn hiểu rõ hơn về hệ thống và các công cụ Marketing \r\ntrên Mạng xã hội. Mạng Cộng đồng SuKienHay.com hân hạnh giới thiệu \r\nchương trình đặc biệt.</strong></p>\r\n<p style="text-align: center;">\r\n	<br>\r\n	<img alt="RichdadLoc | Hệ thống Mạng xã hội" src="http://farm8.staticflickr.com/7192/7046567641_c50c770ae7_b.jpg" style="width: 510px;"></p>\r\n<h3 style="color: red; text-align: center;">\r\n	<span style="font-size:14px;"><strong>CHƯƠNG TRÌNH ĐẶC BIỆT MANG ĐẾN CHO BẠN</strong></span><br>\r\n	&nbsp;</h3>\r\n<p>\r\n	<strong><span style="color:#ff0000;"><span style="background-color:#ffff00;">Tại Hà Nội:</span></span></strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="color:#0000ff;"><strong>Thời gian: 1</strong>8h30 - 21h00 ngày 9/4/2012<br>\r\n	<strong>Địa điểm: </strong>Tầng 9, Trung tâm Thương Mại ASAHA, 149A Phố Huế, Hoàn Kiếm, Hà Nội</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><span style="color:#ff0000;"><span style="background-color:#ffff00;">Tại TP HCM:</span></span></strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="color:#0000cd;"><strong>Thời gian: </strong>18h30 - 21h00 ngày 11/4/2012</span></p>\r\n<p>\r\n	<span style="color:#0000cd;"><strong>Địa điểm: </strong>Hội trường VIP 203, Tòa nhà Itaxa, 126 Nguyễn Thị Minh Khai, Phường 6, Q3</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="color: rgb(204, 0, 0);" align="center">\r\n	<span style="font-size:14px;"><strong><span style="background-color:#ffff00;">Hội thảo tại TP HCM vui lòng xem thông tin và đăng ký tại đây</span></strong><span style="background-color:#ffff00;">:</span></span></p>\r\n<p style="color: rgb(204, 0, 0);" align="center">\r\n	<a href="http://sukienhay.com/Xay-dung-he-thong-Marketing-voi-Mang-xa-hoi-1810-2011.html"><span style="background-color:#ffff00;">http://sukienhay.com/Xay-dung-he-thong-Marketing-voi-Mang-xa-hoi-1810-2011.html</span></a></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="color:#ff0000;"><strong>Diễn giả:</strong></span></p>\r\n<p>\r\n	<br>\r\n	<span style="color:#000080;"><strong>Mr. RichdadLoc </strong>– <em>Chuyên\r\n gia Internet Marketing với 9 năm kinh nghiệm. Hiện anh là chủ tịch của \r\nLinkGroup, EasyMedia, ONNET và đồng sáng lập mạng cộng đồng \r\nSuKienHay.com</em></span></p>\r\n<p>\r\n	<br>\r\n	<img alt="alt" src="http://farm7.staticflickr.com/6228/6852817654_ff8f862d04.jpg" style="width: 150px; height: 216px; margin: 5px; float: left;">RichdadLoc\r\n được biết đến như một doanh nhân trong thời đại mới. Anh là người gây \r\nảnh hưởng lớn đến Internet Marketing của Việt Nam với hàng trăm chương \r\ntrình hội thảo, khóa học và các công cụ phần mềm. Các chương trình của \r\nanh đã có hàng chục nghìn người tham dự trong suốt 03 năm qua. Anh có \r\nniềm đam mê thực sự vào Internet và đã chứng minh thực tế là anh không \r\nsợ học tập thông qua những thử nghiệm mắc lỗi và đúc rút kinh nghiệm. \r\nAnh là người đầu tiên và duy nhất của Việt Nam là tác giả của 3 cuốn \r\nsách Internet Marketing. Với sứ mệnh của mình, anh luôn cố gắng và nỗ \r\nlực tối đa để chia sẻ cho kiến thức cho nhiều người hơn nữa.</p>\r\n<p>\r\n	<br>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<br>\r\n	<span style="color:#000080;"><strong>Ms. Lê Thúy Hạnh&nbsp;</strong> - <em>Chuyên gia hàng đầu về truyền thông mạng xã hội, CEO của Digimarketing JSC, nữ hoàng tên miền Việt Nam.</em></span></p>\r\n<p>\r\n	<br>\r\n	<img alt="alt" src="http://emotino.com/data/avatar/lethuyhanh18_5941.jpg" style="width: 150px; height: 166px; float: left; margin: 5px; border-width: 0pt; border-style: solid;">Lê\r\n Thúy Hạnh được biết đến như một nữ doanh nhân thành đạt với lĩnh vực \r\ntiếp thị số. Chị đã đưa công ty DigiMarketing trở thành một công ty \r\nchuyên nghiệp về tiếp thị số, truyền thông trực tuyến. Digimarketing là \r\nđơn vị cung cấp các giải pháp tiếp thị hiệu qủa cho các doanh nghiệp, cá\r\n nhân muốn mở rộng quảng bá thương hiệu trên các môi trường số hoá và \r\ninternet. Chị đồng thời là tác giả của nhiều bài báo về tiếp thị số trên\r\n những tờ báo lớn như Nhịp cầu đầu tư, Thương hiệu và doanh nghiệp…</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="alt" src="http://farm8.staticflickr.com/7107/6852794186_6371f1fd66_b.jpg" style="width: 510px; height: 335px;"><br>\r\n	<br>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="color: rgb(255, 0, 0);"><strong>Bạn sẽ học được gì từ hội thảo này?</strong></span></span></p>\r\n<p>\r\n	<br>\r\n	<em>- Công thức thành công với người làm Internet Marketing.<br>\r\n	- Xây dựng hệ thống Marketing qua mạng xã hội với chi phí bằng "0”.<br>\r\n	- Xu hướng tiếp thị Internet năm 2012 và những công cụ hữu ích.<br>\r\n	- Phương pháp Marketing và kinh doanh hiệu quả qua mạng xã hội.<br>\r\n	- Cách thu hút hàng chục nghìn lượt truy cập / ngày vào website qua MXH.<br>\r\n	- Truyền thông mới và cách tạo ra sức lan truyền.<br>\r\n	- Xây dựng thương hiệu nhanh chóng và hiệu quả với mạng xã hội.</em></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<span style="font-size: medium;"><strong>PHÍ THAM DỰ: 300.000 ĐỒNG</strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="color: #ff0000;"><span style="font-size: large;"><span style="background-color:#ffff00;">SuKienHay.Com tặng vé cho 100 người đầu tiên</span></span></span></p>\r\n<p style="text-align: center;">\r\n	<span style="color: rgb(255, 0, 0);"><span style="font-size: large;"><span style="background-color: rgb(255, 255, 0);">(Ưu tiên cho những người làm về Kinh doanh, Marketing, Quản lý)</span></span></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size:14px;"><span style="color: rgb(0, 0, 0);"><em>(Thu tại quầy đăng ký hội thảo)</em></span></span></p>\r\n<p style="text-align: center;">\r\n	<img alt="loading-icon" src="http://sukienhay.com/images/stories/icon/loading-icon.gif" title="loading-icon" border="0" height="11" width="43"></p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<span style="color: #0000ff;"><strong>Hãy nhanh tay để có chỗ trong chương trình của chúng tôi</strong></span></p>\r\n<p style="text-align: center;">\r\n	<span style="color: #ff0000;"><strong>Chương trình đã thay đổi hàng nghìn người. Bạn sẽ là người tiếp theo ?</strong></span></p>\r\n<p style="color: #cc0000;" align="center">\r\n	<a href="https://docs.google.com/a/sukienhay.com/spreadsheet/viewform?formkey=dHpld1ZiMERhdXUzX3BLMldPMERFNmc6MQ" target="_blank"><span style="font-size: small;"><strong><img alt="dangky-sukienhay" src="http://sukienhay.com/images/stories/logo/dangky-sukienhay.png" style="border: 0px none currentcolor;" title="dangky-sukienhay" border="0" height="75" width="233"></strong></span></a></p>\r\n<p style="color: rgb(204, 0, 0);" align="center">\r\n	&nbsp;</p>\r\n<p style="color: rgb(204, 0, 0);" align="center">\r\n	<span style="font-size:14px;"><strong><span style="background-color:#ffff00;">Hội thảo tại TP HCM vui lòng xem thông tin và đăng ký tại đây</span></strong><span style="background-color:#ffff00;">:</span></span></p>\r\n<p style="color: rgb(204, 0, 0);" align="center">\r\n	<a href="http://sukienhay.com/Xay-dung-he-thong-Marketing-voi-Mang-xa-hoi-1810-2011.html"><span style="background-color:#ffff00;">http://sukienhay.com/Xay-dung-he-thong-Marketing-voi-Mang-xa-hoi-1810-2011.html</span></a></p>\r\n<p style="color: rgb(204, 0, 0);" align="center">\r\n	&nbsp;</p>\r\n<h1 style="text-align: center;">\r\n	<span style="color: rgb(255, 102, 0);"><span style="font-size: small;"><span style="font-family: arial,helvetica,sans-serif;"><strong style="color: rgb(0, 0, 0);"><span style="font-size: large;">Gọi số: 04 3565 1974 / </span></strong></span></span></span><span style="color: #ff6600;"><span style="font-size: small;"><span style="font-family: arial,helvetica,sans-serif;"><strong style="color: #000000;"><span style="font-size: large;">0948 11 44 71 (Ms Huyền</span></strong></span></span></span></h1>\r\n<p align="left">\r\n	<span style="font-size: small;">Mong gặp bạn tại chương trình:</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p align="center">\r\n	<span style="font-size: small;"><strong>MẠNG CỘNG ĐỒNG SUKIENHAY.COM - NÂNG TÂM GIÁ TRỊ VIỆT</strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p align="center">\r\n	<span style="font-size: small;"><strong>Add: </strong>Tòa nhà SuKienHay, Số 20/176 Lê Trọng Tấn, Thanh Xuân, Hà Nội<br>\r\n	<strong>Tel: </strong>04 3565 1974 (25 lines) * <strong>Fax:</strong> 04 6276 4656<br>\r\n	<strong>Email: </strong>info@sukienhay.com * <strong>Website: </strong>www.sukienhay.com</span></p>\r\n<p align="center">\r\n	&nbsp;</p>\r\n<p>\r\n	<u><span style="color:#ff0000;"><strong><span style="font-size: small;">Một số hình ảnh hội thảo chúng tôi đã tổ chức</span></strong>:</span></u></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="alt" src="http://farm8.staticflickr.com/7244/7004378257_a61c91854b_z.jpg" style="width: 510px; height: 339px;"></p>\r\n<p style="text-align: center;">\r\n	<img alt="RichdadLoc | Internet Marketing Guru" src="http://farm8.staticflickr.com/7209/7033960231_e4be58b99d_z.jpg" style="width: 510px; height: 339px;"></p>\r\n<p style="text-align: center;">\r\n	<img alt="RichdadLoc | Chuyên gia Internet Marketing" src="http://farm8.staticflickr.com/7070/6887867134_e91c22abaf_z.jpg" style="width: 510px; height: 339px;"></p>\r\n<p style="text-align: center;">\r\n	<img alt="RichdadLoc | Internet Marketing Guru" src="http://farm8.staticflickr.com/7128/6858271968_082bbfff13_z.jpg" style="width: 510px; height: 339px;"></p>\r\n<p style="text-align: center;">\r\n	<img alt="alt" src="http://farm8.staticflickr.com/7054/7004387703_4fafbcd19e_z.jpg" style="width: 510px; height: 339px;"></p>\r\n<p style="text-align: center;">\r\n	<strong>Các diễn giả / chuyên gia Internet Marketing hàng đầu của Việt Nam</strong></p>			\r\n				</div>', 'Hà N?i', '34e21ad5d6453bf800ec2d53.jpg', '2012-04-07 06:00:00', '2012-04-07 06:00:00', 10000, 'lớn', '2012-04-07 11:25:51', '2012-04-07 11:25:55', 1, 1, 0, 0, 0, 0);
INSERT INTO `tbl_event` (`id`, `typeevent_id`, `name`, `content`, `place`, `thumbnail`, `start_time`, `end_time`, `number_guest`, `dimention`, `create_time`, `update_time`, `create_user_id`, `view`, `censor`, `slide_small`, `slide_lage`, `e_like`) VALUES
(5, 1, '[ĐẶC BIỆT] Làm chủ hệ thống Internet Marketing', '<div class="ctitle">Thông tin sự kiện<b> [ĐẶC BIỆT] Làm chủ hệ thống Internet Marketing</b></div>\r\n						<div>\r\n	<strong><span style="font-size: medium;"><span class="heading1">Một cơ hội tuyệt vời để bạn tìm hiểu</span></span></strong></div>\r\n<div>\r\n	<br>\r\n	<span style="color: #ff0000;"><span style="font-size: medium;"><span class="style15">Làm\r\n thế nào để xây dựng một hệ thống Internet Marketing NHANH CHÓNG, BỀN \r\nVỮNG mang lại lợi nhuận cao và tận hưởng cuộc sống mà bạn mong muốn !</span></span></span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<p class="basictext" style="text-align: left;">\r\n	<span style="color: #0000ff;"><strong>Nếu bạn muốn tận mắt chứng kiến \r\ntừng bước những phương pháp mà các chuyện gia đã áp dụng để tạo ra hệ \r\nthống kinh doanh và marketing nhanh chóng với lợi nhuận cao… thì hãy ĐẶT\r\n CHỖ NGAY BÂY GIỜ!</strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="font-size: small;"><span style="font-family: Arial;"><strong>Khóa học Internet Marketing Mastery - Làm chủ hệ thống Internet Marketing </strong>được\r\n xây dựng nhằm cung cấp kiến thức, kỹ năng và tuyệt chiêu cùng những \r\nkinh nghiệm hữu ích cho việc khai thác các công cụ kinh doanh và tiếp \r\nthị trên Internet. Công cụ chúng tôi hoàn toàn MIỄN PHÍ và TỰ ĐỘNG.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img src="http://sukienhay.com/images/banners/internetmarketing/banner_01.jpg" border="0" height="347" width="660"></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="font-size: small;"><span style="font-family: Arial;">Hoàn \r\nthành khóa học Internet Marketing này, bạn có thể hoàn toàn tự tin để \r\nbước vào thế giới Internet Marketing bằng khả năng quản lý chiến dịch, \r\ntriển khai kế hoạch và sử dụng các cộng cụ Internet Marketing qua mạng \r\nInternet một cách tốt nhất.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<span style="color: #0000ff;"><span style="font-family: Arial; font-size: x-small;"><span style="font-size: small;"><em><strong>"Khóa\r\n học đã làm thay đổi công việc kinh doanh của hàng nghìn người. Còn bạn,\r\n bạn muốn thay đổi nhưng chưa hành động. ĐIỀU QUAN TRỌNG. Bạn hãy HÀNH \r\nĐỘNG để có một chỗ ngồi trong chương trình ĐẶC BIỆT này trước khi hết \r\nchỗ."</strong></em></span></span></span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<p>\r\n		<span style="color:#ff0000;"><strong>Tại Hà Nội</strong>:</span></p>\r\n	<p>\r\n		<span style="color:#0000cd;"><strong>Thời gian: </strong>8h30 - 21h00 ngày 21 &amp; 22/4/2012</span></p>\r\n	<p>\r\n		<span style="color:#0000cd;"><strong>Địa điểm: </strong>Tòa Nhà SuKienHay, 20/176 Lê Trọng Tấn, Thanh Xuân, Hà Nội</span></p>\r\n	<p>\r\n		&nbsp;</p>\r\n	<p>\r\n		<span style="color:#ff0000;"><strong>Tại TP HCM</strong>:</span></p>\r\n	<p>\r\n		<span style="color:#0000cd;"><strong>Thời gian:</strong> 8h30 - 21h00 ngày 14 &amp; 15/4/2012</span></p>\r\n	<p>\r\n		<span style="color:#0000cd;"><strong>Địa điểm: </strong>Lầu 10, Khách Sạn Sài Gòn, số 41 đường Đông Du, Q.1,Tp.HCM</span></p>\r\n</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div style="text-align: center;">\r\n	<span style="color: #0000ff;"><span style="font-family: Arial; font-size: x-small;"><span style="font-size: small;"><em><strong><img alt="Khóa học Internet Marketing | Học Internet Marketing " src="http://www.sukienhay.com/images/stories/banners/2012/t3/banner_02.jpg" style="border-width: 0pt; border-style: solid; width: 510px; height: 280px;"></strong></em></span></span></span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<p>\r\n		<span style="font-size: small;"><span style="font-family: Arial;"><span style="color: #ff0000;"><strong>Đối tượng đào tạo:</strong></span></span></span></p>\r\n</div>\r\n<div>\r\n	<br>\r\n	<span style="font-size: small;"><span style="font-family: Arial;">• Bộ phận Marketing, Sales.<br>\r\n	• Những cá nhân muốn học về kinh doanh và tiếp thị trên Internet.<br>\r\n	• Các doanh nghiệp nhỏ, ít vốn<br>\r\n	• Các DN đang gặp khó khăn về tăng Doanh số về giải pháp bán hàng.</span></span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<span style="font-size: small;"><span style="font-family: Arial;"><span style="color: #ff0000;"><strong>Yêu cầu trước khi đào tạo:</strong></span></span></span></div>\r\n<div>\r\n	<br>\r\n	<span style="font-size: small;"><span style="font-family: Arial;">• Sử dụng được máy tính và Internet.<br>\r\n	• Không nhất thiết phải biết CNTT</span></span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<p>\r\n		<span style="color: #ff0000;"><span style="color: #008080;"><strong>Module1: Internet Business Mastery - Làm chủ hệ thống Kinh doanh trên Internet:</strong></span></span></p>\r\n</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<img src="http://sukienhay.com/images/banners/internetmarketing/banner_04.jpg" border="0"></div>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="color: #ff0000;"><strong>Bạn sẽ học được gì từ Module Internet Business Mastery này?</strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	• Cách thức xây dựng hệ thống website.</p>\r\n<p>\r\n	• Hướng dẫn tạo website ấn tượng, chuyên nghiệp HOÀN TOÀN MIỄN PHÍ.</p>\r\n<p>\r\n	• Kiến thức về Tên miền, đầu tư tên miền và bí mật lựa chọn tên miền đẹp.</p>\r\n<p>\r\n	• Hướng dẫn các tuyệt chiêu kinh doanh trên Internet.</p>\r\n<p>\r\n	• Phương pháp tăng doanh số bán hàng lên 200% và nhiều hơn nữa.</p>\r\n<p>\r\n	• Tăng tỷ lệ mua hàng qua website một cách nhanh chóng và hiệu quả.</p>\r\n<p>\r\n	• Các nguồn thu từ website và bạn có thể làm được.</p>\r\n<p>\r\n	• Làm sao để website của bạn lên top đầu google nhanh chóng.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="color: #ff0000;"><span style="color: #008080;"><strong><span style="color: #0066ff;">Module 2: Social Media Marketing Mastery - Làm chủ hệ thống Marketing với Mạng xã hội</span></strong></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<div>\r\n	<img src="http://sukienhay.com/images/banners/internetmarketing/banner_05.jpg" border="0"></div>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="color: #ff0000;"><strong>Bạn sẽ học được gì từ Module này?</strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	• Cách thức xây dựng hệ thống vòng tròn Mạng xã hội Facebook, Youtube, Twitter...<br>\r\n	• Phương pháp để vượt qua đối thủ khác kể cả khi website của bạn mới ra đời.<br>\r\n	• Cách viết bài copywriting thu hút hàng nghìn người đọc.<br>\r\n	• Cách thức chuyển đổi người dùng Mạng xã hội thành khách hàng website.<br>\r\n	• Cách thức để website có hàng nghìn người truy cập / ngày với chi phí "0".<br>\r\n	• Phương pháp để video của bạn có hàng chục nghìn người xem trong thời gian ngắn.<br>\r\n	• Tuyệt chiêu bán hàng qua mạng xã hội và bạn cũng có thể làm được.<br>\r\n	• Xây dựng thương hiệu qua mạng xã hội nhanh chóng và hiệu qủa.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="color: #ff0000;"><span style="color: #008080;"><strong><span style="color: #0066ff;"><span style="color: #800000;">Module 3: Email Marketing Mastery - Làm chủ hệ thống Email Marketing</span></span></strong></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<div>\r\n	<img src="http://sukienhay.com/images/banners/internetmarketing/banner_06.jpg" border="0"></div>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="color: #ff0000;"><strong>Bạn sẽ học được gì từ Module&nbsp; này?</strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	• Cách xây dựng quy trình chiến dịch Email Marketing hiệu quả nhất<br>\r\n	• Hướng dẫn viết nội dung email thu hút nhiều người đọc.<br>\r\n	• Tăng doanh số bán hàng nhanh chóng qua Email Marketing.<br>\r\n	• Hướng dẫn tự thiết kế Email Marketing bằng HTML<br>\r\n	• Xây dựng danh sách email nhanh chóng và hiệu quả.<br>\r\n	• Tạo FreeGift và form thu thập khách hàng<br>\r\n	• Tiếp thị lan truyền bằng Email Marketing.<br>\r\n	• Hướng dẫn sử dụng phần mềm gửi Email chuyên nghiệp.<br>\r\n	• Thống kê và đo lường hiệu quả sau mỗi chiến dịch Email Marketing.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="font-family: Arial; color: #ff0000; font-size: x-small;"><span style="font-size: small;"><strong>Giảng viên:</strong></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<div>\r\n	<div>\r\n		<p class="MsoNormal">\r\n			<span style="font-size: small;"><strong><span style="font-family: Arial;">Mr. RichdadLoc - TGĐ EasyMedia Group </span></strong></span></p>\r\n		<p class="MsoNormal" style="text-align: justify;">\r\n			<span style="font-size: small;"><em><span style="line-height: 115%;">Chủ tịch HĐQT EasyMedia Group &amp; ONNET., JSC</span></em></span></p>\r\n		<p class="MsoNormal" style="text-align: justify;">\r\n			<span style="font-size: small;"><em><span style="line-height: 115%;">Đồng sáng lập Mạng cộng đồng chia sẻ sự kiện lớn nhất Việt Nam - SuKienHay.Com</span></em></span></p>\r\n		<p class="MsoNormal" style="text-align: justify;">\r\n			<span style="font-size: small;"><em><span style="line-height: 115%;">Chuyên gia &amp; Diễn giả Internet Marketing hàng đầu</span></em></span></p>\r\n		<p class="MsoNormal" style="text-align: justify;">\r\n			&nbsp;</p>\r\n		<p class="MsoNormal" style="text-align: center;">\r\n			<span style="font-size: small;"><strong><a href="http://www.youtube.com/richdadloc" target="_blank"><span style="font-family: Arial;"><img src="http://sukienhay.com/images/banners/button_video_richdadloc.jpg" border="0" height="58" width="367"></span></a></strong></span></p>\r\n		<p class="MsoNormal" align="center">\r\n			&nbsp;</p>\r\n		<p class="MsoNormal" align="center">\r\n			<img src="http://sukienhay.com/images/banners/internetmarketing/diengia.jpg" style="border: 0pt none;" border="0" height="377" width="550"></p>\r\n		<p class="MsoNormal" style="text-align: justify;">\r\n			&nbsp;</p>\r\n		<p class="MsoNormal" style="text-align: justify;">\r\n			<span style="font-size: small;"><span style="line-height: 115%;">RichdadLoc\r\n được ghi nhận là một nhà đào tạo có ảnh hưởng lớn đến Internet \r\nMarketing của Việt Nam. Với những đóng góp thành công của mình anh đã \r\nchứng minh thực tế là anh không sợ học tập thông qua những thử nghiệm \r\nmắc lỗi và đúc rút kinh nghiệm.</span></span></p>\r\n		<p class="MsoNormal" style="text-align: justify;">\r\n			&nbsp;</p>\r\n		<p>\r\n			<span style="font-size: small;"><span style="line-height: 115%;">RichdadLoc\r\n hoàn toàn tin tưởng vào thực tế là để đạt được thành công trên mạng, \r\nmột yếu tố quan trọng đó chính là tư duy của bạn và anh có một niềm đam \r\nmê là chia sẻ cách thức suy nghĩ của mình cho nhiều người từ góc độ của \r\nmột người làm Internet Marketing thành công.</span></span></p>\r\n		<p>\r\n			&nbsp;</p>\r\n		<p>\r\n			<span style="font-size: small;"><span style="line-height: 115%;">Anh \r\ncũng là minh chứng cho thực tế là thứ nhất Internet Marketing là phương \r\nthức hiệu quả nhất để tạo ra một phong cách sống mà bạn mong muốn và \r\ncông cụ này cũng mang đến cho bạn sức mạnh để chọn lựa và thứ hai \r\nInternet Marketing có thể mang đến cho bạn sự tự do về tiền bạc.</span></span></p>\r\n		<p class="MsoNormal" align="center">\r\n			&nbsp;</p>\r\n		<p class="MsoNormal">\r\n			<span style="font-family: Arial; color: #ff0000; font-size: x-small;"><span style="font-size: small;"><strong>Một số hình ảnh khóa học đã tổ chức:</strong></span></span></p>\r\n		<p class="MsoNormal" align="center">\r\n			<img alt="hình ảnh khóa học RichdadLoc" src="http://sukienhay.com/images/stories/banners/2012/t3/khoahocdatochuc.jpg" style="border: 0pt none;" title="Richdadloc | Hình ảnh richdadloc | Richdadloc Internet" border="0"></p>\r\n		<p>\r\n			<strong><span style="font-family: Arial; color: #ff0000; font-size: x-small;"><span style="font-size: small;">Đánh giá từ học viên đã tham dự khóa học:</span></span></strong></p>\r\n		<p align="center">\r\n			<img src="http://www.internetmarketing.vn/backup/khoahocinternetmarketing/khoa10/images/danhgiatuhocvien.jpg" style="border: 0pt none;" border="0"></p>\r\n		<p align="center">\r\n			<a href="http://easymedia.vn/lien-he.html" targe